import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, useLocation, Navigate } from "react-router-dom";
import Dashboard1 from "./components/Dashboard1";
import Home from "./components/Home";
import About from "./components/About";
import Notes from "./components/Notes";
import Profile from "./components/Profile";
import Login from "./components/Login";
import Register from "./components/Register";
import ProfileCreation from "./components/ProfileCreation";
import NoteDetails from "./components/NoteDetails";
import FAQ from "./components/FAQ";

// Import Admin Components
import AdminDashboard from "./components/admin/AdminDashboard";
import ManageNotes from "./components/admin/ManageNotes";
import AddNote from "./components/admin/AddNote";
import EditNote from "./components/admin/EditNote";

// Import ProtectedRoute
import ProtectedRoute from "./components/ProtectedRoute";
import AdminProtectedRoute from "./components/AdminProtectedRoute";

const App = () => {
  const [username, setUsername] = useState(localStorage.getItem("username") || null);
  const [isAdmin, setIsAdmin] = useState(localStorage.getItem("isAdmin") === "true");
  const [notes, setNotes] = useState(getLocalStorageItem("notes", []));
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const handleStorageChange = () => {
      setUsername(localStorage.getItem("username") || null);
      setIsAdmin(localStorage.getItem("isAdmin") === "true");
    };

    window.addEventListener("storage", handleStorageChange);
    return () => window.removeEventListener("storage", handleStorageChange);
  }, []);

  const addNote = (newNote) => {
    const updatedNotes = [...notes, newNote];
    setNotes(updatedNotes);
    setLocalStorageItem("notes", updatedNotes);
  };

  const deleteNote = (id) => {
    const updatedNotes = notes.filter((note) => note.id !== id);
    setNotes(updatedNotes);
    setLocalStorageItem("notes", updatedNotes);
  };

  const updateNote = (updatedNote) => {
    const updatedNotes = notes.map((note) =>
      note.id === updatedNote.id ? updatedNote : note
    );
    setNotes(updatedNotes);
    setLocalStorageItem("notes", updatedNotes);
  };

  useEffect(() => {
    // Simulate fetching notes
    setTimeout(() => {
      setIsLoading(false);
    }, 1000);
  }, []);

  if (isLoading) {
    return <p>Loading notes...</p>;
  }

  return (
    <Router>
      <AppRoutes
        username={username}
        setUsername={setUsername}
        isAdmin={isAdmin}
        notes={notes}
        setNotes={setNotes}
        addNote={addNote}
        deleteNote={deleteNote}
        updateNote={updateNote}
      />
    </Router>
  );
};

const AppRoutes = ({ username, setUsername, isAdmin, notes, setNotes, addNote, deleteNote, updateNote }) => {
  const location = useLocation();

  return (
    <>
      <Routes>
        {/* Public Routes */}
        <Route path="/" element={<Dashboard1 username={username} setUsername={setUsername} />}>
          <Route index element={<Home />} />
          <Route path="about" element={<About />} />
          <Route path="notes" element={<Notes notes={notes} />} />
          <Route path="notes/:noteId" element={<NoteDetails notes={notes} />} />
          <Route path="login" element={<Login setUsername={setUsername} />} />
          <Route path="register" element={<Register setUsername={setUsername} />} />
        </Route>

        {/* Protected Routes (Requires Login) */}
        <Route element={<ProtectedRoute username={username} />}>
          <Route path="profile" element={<Profile />} />
          <Route path="create-profile" element={<ProfileCreation />} />
        </Route>

        {/* Admin Routes (Requires Admin Access) */}
        <Route element={<AdminProtectedRoute isAdmin={isAdmin} />}>
          <Route path="/admin" element={<AdminDashboard />} />
          <Route path="/admin/manage-notes" element={<ManageNotes notes={notes} deleteNote={deleteNote} />} />
          <Route path="/admin/add-note" element={<AddNote addNote={addNote} />} />
          <Route path="/admin/edit-note/:noteId" element={<EditNote notes={notes} updateNote={updateNote} />} />
        </Route>

        {/* Redirect unknown routes */}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>

      {/* Show FAQ only on the Home page */}
      {location.pathname === "/" && <FAQ />}
    </>
  );
};

const getLocalStorageItem = (key, defaultValue) => {
  try {
    const item = localStorage.getItem(key);
    return item ? JSON.parse(item) : defaultValue;
  } catch (error) {
    console.error("Error accessing localStorage:", error);
    return defaultValue;
  }
};

const setLocalStorageItem = (key, value) => {
  try {
    localStorage.setItem(key, JSON.stringify(value));
  } catch (error) {
    console.error("Error saving to localStorage:", error);
  }
};

export default App;
