import React, { useState, useEffect } from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";
import { FaUserCircle, FaBars, FaTimes, FaMoon, FaSun } from "react-icons/fa";
import API from "../api"; // Import Axios instance
import "./Dashboard.css";

const Dashboard1 = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [darkMode, setDarkMode] = useState(() => localStorage.getItem("darkMode") === "true");
  const [username, setUsername] = useState(localStorage.getItem("username") || "");
  const [role, setRole] = useState("");
  const [notes, setNotes] = useState([]); // State to store notes

  const navigate = useNavigate();

  useEffect(() => {
    const storedRole = localStorage.getItem("role")?.trim().toLowerCase();
    setRole(storedRole || "");
  }, []);

  useEffect(() => {
    document.body.classList.toggle("dark-mode", darkMode);
    localStorage.setItem("darkMode", darkMode);
  }, [darkMode]);

  // Fetch Notes from API
  useEffect(() => {
    API.get("/notes")
      .then((res) => setNotes(res.data))
      .catch((err) => console.error("Error fetching notes:", err));
  }, []);

  const toggleMenu = () => setMenuOpen(!menuOpen);
  const toggleDarkMode = () => setDarkMode((prev) => !prev);

  const handleLogout = () => {
    localStorage.removeItem("username");
    localStorage.removeItem("role");
    localStorage.removeItem("token");
    setUsername("");
    setRole("");
    navigate("/login");
    window.location.reload();
  };

  return (
    <div className={`dashboard-container ${darkMode ? "dark-mode" : ""}`}>
      <header className="taskbar">
        <div className="taskbar-logo">
          <h1>UniVerse</h1>
        </div>

        <nav className={`taskbar-nav ${menuOpen ? "open" : ""}`}>
          <ul>
            <li><Link to="/">Home</Link></li>
            <li><Link to="about">About</Link></li>
            <li><Link to="notes">Notes</Link></li>
            {role === "admin" && <li><Link to="/admin">Admin Panel</Link></li>}
            {username ? (
              <li className="profile-dropdown">
                <span className="profile-trigger">
                  <FaUserCircle className="profile-icon" />
                  {username}
                </span>
                <ul className="dropdown-menu">
                  <li onClick={() => navigate("/profile")}>Profile</li>
                  <li onClick={() => navigate("/settings")}>Settings</li>
                  <li onClick={handleLogout}>Logout</li>
                </ul>
              </li>
            ) : (
              <>
                <li><Link to="login">Login</Link></li>
                <li><Link to="register">Sign Up</Link></li>
              </>
            )}
            <li className="dark-mode-toggle" onClick={toggleDarkMode}>
              {darkMode ? <FaSun /> : <FaMoon />}
            </li>
          </ul>
        </nav>

        <div className="menu-toggle" onClick={toggleMenu}>
          {menuOpen ? <FaTimes /> : <FaBars />}
        </div>
      </header>

      <main className="main-content">
        <Outlet />

        {/* Notes Section */}
        <section className="notes-section">
          <h2 className="notes-heading">Available Notes</h2>
          <div className="notes-container">
            {notes.length > 0 ? (
              notes.map((note) => (
                <div key={note._id} className="note-card">
                  <h3 className="note-title">{note.title}</h3>
                  <p className="note-content" dangerouslySetInnerHTML={{ __html: note.content }} />
                  {note.fileUrl && (
                    <a href={`http://localhost:5000${note.fileUrl}`} download className="download-link">
                      ðŸ“¥ Download File
                    </a>
                  )}
                </div>
              ))
            ) : (
              <p className="no-notes-message">No notes available.</p>
            )}
          </div>
        </section>
      </main>
    </div>
  );
};

export default Dashboard1;
