import React, { useState, useEffect } from "react";
import { Editor } from "@tinymce/tinymce-react";
import { useNavigate } from "react-router-dom";
import sanitizeHtml from "sanitize-html";
import "../css/NoteManager.css";

const NoteManager = () => {
  const navigate = useNavigate();
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [note, setNote] = useState({ title: "", content: "" });
  const [editingNoteId, setEditingNoteId] = useState(null);

  useEffect(() => {
    const storedNotes = JSON.parse(localStorage.getItem("notes")) || [];
    console.log("Initial stored notes:", storedNotes); // Debug initial load
    setNotes(storedNotes);
    setLoading(false);
  }, []);

  const saveNote = () => {
    const cleanedContent = sanitizeHtml(note.content, {
      allowedTags: ["p", "b", "i", "em", "strong", "ul", "ol", "li", "a"],
      allowedAttributes: { a: ["href"] },
    });
    console.log("Saving cleaned content:", cleanedContent); // Debug sanitized content

    const updatedNotes = [...notes];
    const newNote = { ...note, id: Date.now(), content: cleanedContent };

    if (editingNoteId) {
      const index = notes.findIndex((n) => n.id === editingNoteId);
      updatedNotes[index] = newNote;
      console.log("Updated existing note:", newNote); // Debug edit
    } else {
      updatedNotes.unshift(newNote);
      console.log("Added new note:", newNote); // Debug add
    }

    localStorage.setItem("notes", JSON.stringify(updatedNotes));
    setNotes(updatedNotes);
    setNote({ title: "", content: "" });
    setEditingNoteId(null);
  };

  const editNote = (note) => {
    console.log("Editing note:", note); // Debug edit selection
    setEditingNoteId(note.id);
    setNote({ title: note.title, content: note.content });
  };

  const deleteNote = (noteId) => {
    console.log("Deleting note with ID:", noteId); // Debug delete
    const updatedNotes = notes.filter((n) => n.id !== noteId);
    localStorage.setItem("notes", JSON.stringify(updatedNotes));
    setNotes(updatedNotes);
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="note-manager-container">
      <h2>Note Manager</h2>

      <div className="note-editor">
        <input
          type="text"
          placeholder="Title"
          value={note.title}
          onChange={(e) => setNote({ ...note, title: e.target.value })}
          className="note-title-input"
        />
        <Editor
          apiKey="qks55it6scopg5ibx5k982jzxunxu1x79rdlzkmx2yapv5bb"
          value={note.content}
          onEditorChange={(content) => setNote({ ...note, content })}
          init={{
            height: 250,
            menubar: false,
            plugins: "lists link image table code",
            toolbar: "undo redo | bold italic underline | bullist numlist | link | code",
            content_css: "https://cdn.jsdelivr.net/npm/tinymce@5/skins/ui/oxide/content.css",
          }}
        />
        <button onClick={saveNote} className="save-button">
          {editingNoteId ? "Update Note" : "Add Note"}
        </button>
      </div>

      <h3>All Notes</h3>
      <table className="notes-table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Content</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {notes.map((note) => {
            console.log("Table rendering note content:", note.content); // Debug table render
            return (
              <tr key={note.id}>
                <td>{note.title}</td>
                <td>
                  <div dangerouslySetInnerHTML={{ __html: note.content }} />
                </td>
                <td>
                  <button onClick={() => editNote(note)} className="edit-button">
                    Edit
                  </button>
                  <button
                    onClick={() => deleteNote(note.id)}
                    className="delete-button"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default NoteManager;
