// import * as noteService from '../../utils/noteService';
import React, { useState } from 'react';
import { Editor } from 'react-draft-wysiwyg';
import { EditorState, convertToRaw } from 'draft-js';
import 'react-draft-wysiwyg/dist/react-draft-wysiwyg.css';
import { saveNote } from '../utils/noteService';








const AddNote = () => {
  const [editorState, setEditorState] = useState(EditorState.createEmpty());
  const [title, setTitle] = useState('');
  const [category, setCategory] = useState('General');
  const [tags, setTags] = useState('');
  const [isProtected, setIsProtected] = useState(false);
  const [reminder, setReminder] = useState('');

  const handleSave = () => {
    const content = JSON.stringify(convertToRaw(editorState.getCurrentContent()));
    saveNote({ title, category, tags: tags.split(','), content, isProtected, reminder });
  };

  return (
    <div>
      <h2>Add New Note</h2>
      <input type="text" placeholder="Title" value={title} onChange={(e) => setTitle(e.target.value)} />
      <select value={category} onChange={(e) => setCategory(e.target.value)}>
        <option>General</option>
        <option>Work</option>
        <option>Personal</option>
      </select>
      <input type="text" placeholder="Tags (comma-separated)" value={tags} onChange={(e) => setTags(e.target.value)} />
      <Editor editorState={editorState} onEditorStateChange={setEditorState} />
      <label>
        <input type="checkbox" checked={isProtected} onChange={() => setIsProtected(!isProtected)} />
        Password Protect
      </label>
      <input type="datetime-local" value={reminder} onChange={(e) => setReminder(e.target.value)} />
      <button onClick={handleSave}>Save Note</button>
    </div>
  );
};

export default AddNote;
