import React, { useState, useEffect } from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";
import { FaUserCircle, FaBars, FaTimes, FaMoon, FaSun } from "react-icons/fa";
import { motion, AnimatePresence } from "framer-motion";
import "../css/Dashboard.css";

const Dashboard1 = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [darkMode, setDarkMode] = useState(() => localStorage.getItem("darkMode") === "true");
  const [username, setUsername] = useState(localStorage.getItem("username") || "");
  const [role, setRole] = useState("");

  const navigate = useNavigate();

  useEffect(() => {
    const storedRole = localStorage.getItem("role")?.trim().toLowerCase();
    setRole(storedRole || "");
  }, []);

  useEffect(() => {
    document.body.classList.toggle("dark-mode", darkMode);
    localStorage.setItem("darkMode", darkMode);
  }, [darkMode]);

  const toggleMenu = () => setMenuOpen(!menuOpen);
  const toggleDarkMode = () => setDarkMode((prev) => !prev);

  const handleLogout = () => {
    localStorage.removeItem("username");
    localStorage.removeItem("role");
    localStorage.removeItem("token");
    setUsername("");
    setRole("");
    navigate("/login");
    window.location.reload();
  };

  return (
    <div className={`dashboard-container ${darkMode ? "dark-mode" : ""}`}>
      <header className="taskbar">
        <motion.div
          className="taskbar-logo"
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.5 }}
        >
          <h1>NoteHive</h1>
        </motion.div>

        <motion.nav className={`taskbar-nav ${menuOpen ? "open" : ""}`} animate={{ opacity: 1 }}>
          <ul>
            <li><Link to="/">Home</Link></li>
            <li><Link to="about">About</Link></li>
            <li><Link to="notes">Notes</Link></li>
            <li><Link to="cp-trivia">Trivia & Quizzes</Link></li>

            {role === "admin" && (
              <>
                <li><Link to="/admin">Admin Panel</Link></li>
                <li><Link to="/admin/noteManager">Note Manager</Link></li>
              </>
            )}

            {username ? (
              <motion.li className="profile-dropdown" whileHover={{ scale: 1.1 }}>
                <span className="profile-trigger">
                  <FaUserCircle className="profile-icon" />
                  {username}
                </span>
                <ul className="dropdown-menu">
                  <li onClick={() => navigate("/profile")}>Profile</li>
                  <li onClick={() => navigate("/settings")}>Settings</li>
                  <li onClick={handleLogout}>Logout</li>
                </ul>
              </motion.li>
            ) : (
              <>
                <li><Link to="login">Login</Link></li>
                <li><Link to="register">Sign Up</Link></li>
              </>
            )}

            <li className="dark-mode-toggle" onClick={toggleDarkMode}>
              <motion.div animate={{ rotate: darkMode ? 180 : 0 }} transition={{ duration: 0.3 }}>
                {darkMode ? <FaSun /> : <FaMoon />}
              </motion.div>
            </li>
          </ul>
        </motion.nav>

        <motion.div className="menu-toggle" onClick={toggleMenu} whileTap={{ scale: 0.9 }}>
          {menuOpen ? <FaTimes /> : <FaBars />}
        </motion.div>
      </header>

      <motion.main className="main-content" initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.5 }}>
        <Outlet />
      </motion.main>
    </div>
  );
};

export default Dashboard1;
