// export default AdminPanel;
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import '../css/AdminPanel.css';  

const AdminPanel = () => {
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [deletingUserId, setDeletingUserId] = useState(null); // Track which user is being deleted

  useEffect(() => {
    const userRole = localStorage.getItem("role");
    const token = localStorage.getItem("token");

    if (!userRole || userRole !== "admin" || !token) {
      navigate("/login");
      return;
    }

    fetchUsers(token);
  }, [navigate]);

  // Fetch users
  const fetchUsers = async (token) => {
    try {
      const response = await fetch("http://localhost:5000/api/users", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to fetch users");
      }

      const data = await response.json();
      console.log("üîπ Fetched users:", data);
      setUsers(data);
    } catch (error) {
      console.error("üö® Error fetching users:", error.message);
      alert("Failed to load users: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  // Change user role (promote to admin or degrade to user)
  const changeRole = async (userId, role) => {
    const action = role === "admin" ? "promote to admin" : "degrade to user";
    const confirmAction = window.confirm(
      `Are you sure you want to ${action} this user?`
    );
    if (!confirmAction) return;

    try {
      const token = localStorage.getItem("token");

      if (!token) {
        console.error("‚ùå No token found. User might not be logged in.");
        alert("Session expired. Please log in again.");
        navigate("/login");
        return;
      }

      const endpoint = `http://localhost:5000/api/promote/${userId}`;
      console.log(`üîπ Sending request to ${endpoint} with role: ${role}`);

      const response = await fetch(endpoint, {
        method: "PUT",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify({ role }),
      });

      const data = await response.json();
      console.log("üîπ API Response from role change:", data);

      if (!response.ok) {
        throw new Error(data.message || `Failed to update role to ${role}`);
      }

      // Validate that the role was actually updated in the response
      if (data.user && data.user.role !== role) {
        throw new Error(`Role update failed: Expected role ${role}, but got ${data.user.role}`);
      }

      console.log(`‚úÖ User role updated to ${role} successfully.`);
      alert(`User role updated to ${role} successfully.`);

      // Refresh the user list
      await fetchUsers(token);
    } catch (error) {
      console.error(`‚ùå Failed to update role to ${role}:`, error.message);
      alert(`Failed to update role: ${error.message}`);
    }
  };

  // Delete a user
  const deleteUser = async (userId, username, email) => {
    const confirmDelete = window.confirm(
      `Are you sure you want to delete user "${username}"? This action cannot be undone.`
    );
    if (!confirmDelete) return;

    setDeletingUserId(userId);
    try {
      const token = localStorage.getItem("token");

      if (!token) {
        console.error("‚ùå No token found. User might not be logged in.");
        alert("Session expired. Please log in again.");
        navigate("/login");
        return;
      }

      console.log(`üîπ Sending DELETE request for user ID: ${userId}`);
      const response = await fetch(`http://localhost:5000/api/users/${userId}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email }),
      });

      const data = await response.json();
      console.log("üîπ API Response from delete:", data);

      if (!response.ok) {
        throw new Error(data.message || "Failed to delete user");
      }

      console.log("‚úÖ User deleted successfully.");
      alert(`User "${username}" deleted successfully.`);

      await fetchUsers(token);
    } catch (error) {
      console.error("‚ùå Failed to delete user:", error.message);
      alert(`Failed to delete user: ${error.message}`);
    } finally {
      setDeletingUserId(null);
    }
  };

  if (loading) return <div className="loading-text"><span className="spinner"></span>Loading...</div>;

  // Prevent degrading the last admin
  const adminCount = users.filter(user => user.role === "admin").length;

  return (
    <div className="admin-panel-container">
      <h2>Admin Panel</h2>

      {/* User Management */}
      <h3>User Management</h3>
      <table>
        <thead>
          <tr>
            <th>Username</th>
            <th>Email</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => {
            const canDegrade = user.role === "admin" && adminCount > 1;
            return (
              <tr key={user._id}>
                <td>{user.username}</td>
                <td>{user.email}</td>
                <td>{user.role}</td>
                <td>
                  {canDegrade ? (
                    <button className="degrade-button" onClick={() => changeRole(user._id, "user")}>
                      Degrade to User
                    </button>
                  ) : user.role !== "admin" ? (
                    <button className="promote-button" onClick={() => changeRole(user._id, "admin")}>
                      Promote to Admin
                    </button>
                  ) : null}
                  <button
                    onClick={() => deleteUser(user._id, user.username, user.email)}
                    className="delete-button"
                    disabled={deletingUserId === user._id}
                  >
                    {deletingUserId === user._id ? "Deleting..." : "Delete"}
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default AdminPanel;
