import React, { useState, useEffect } from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";
import { FaUserCircle, FaUserShield, FaBars, FaTimes, FaMoon, FaSun } from "react-icons/fa";
import "./Dashboard.css";

const Dashboard = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [darkMode, setDarkMode] = useState(() => localStorage.getItem("darkMode") === "true");
  
  // Check if user or admin is logged in
  const username = localStorage.getItem("username"); // Regular user
  const adminName = localStorage.getItem("admin"); // Admin user

  const navigate = useNavigate();

  useEffect(() => {
    document.body.classList.toggle("dark-mode", darkMode);
    localStorage.setItem("darkMode", darkMode.toString());
  }, [darkMode]);

  const toggleMenu = () => setMenuOpen((prev) => !prev);
  const toggleDarkMode = () => setDarkMode((prev) => !prev);

  const handleLogout = () => {
    localStorage.removeItem("username");
    localStorage.removeItem("admin");
    localStorage.removeItem("token");
    navigate(adminName ? "/admin-login" : "/login"); // Redirect to appropriate login page
    window.location.reload();
  };

  return (
    <div className={`dashboard-container ${darkMode ? "dark-mode" : ""}`}>
      <header className="taskbar">
        <div className="taskbar-logo">
          <h1>{adminName ? "Admin Panel" : "UniVerse"}</h1>
        </div>

        <nav className={`taskbar-nav ${menuOpen ? "open" : ""}`}>
          <ul>
            <li>
              <Link to={adminName ? "/admin" : "/"} onClick={() => setMenuOpen(false)}>
                {adminName ? "Dashboard" : "Home"}
              </Link>
            </li>

            {adminName ? (
              <>
                <li><Link to="/admin/users" onClick={() => setMenuOpen(false)}>Manage Users</Link></li>
                <li><Link to="/admin/reports" onClick={() => setMenuOpen(false)}>Reports</Link></li>
              </>
            ) : (
              <>
                <li><Link to="/about" onClick={() => setMenuOpen(false)}>About</Link></li>
                <li><Link to="/notes" onClick={() => setMenuOpen(false)}>Notes</Link></li>
              </>
            )}

            {(username || adminName) ? (
              <li className="profile-dropdown">
                <span className="profile-trigger">
                  {adminName ? <FaUserShield className="profile-icon" /> : <FaUserCircle className="profile-icon" />}
                  {adminName || username}
                </span>
                <ul className="dropdown-menu">
                  <li onClick={() => { navigate(adminName ? "/admin/profile" : "/profile"); setMenuOpen(false); }}>Profile</li>
                  <li onClick={handleLogout}>Logout</li>
                </ul>
              </li>
            ) : (
              <>
                <li><Link to="/login" onClick={() => setMenuOpen(false)}>Login</Link></li>
                <li><Link to="/register" onClick={() => setMenuOpen(false)}>Sign Up</Link></li>
              </>
            )}

            <li className="dark-mode-toggle" onClick={toggleDarkMode}>
              {darkMode ? <FaSun /> : <FaMoon />}
            </li>
          </ul>
        </nav>

        <div className="menu-toggle" onClick={toggleMenu}>
          {menuOpen ? <FaTimes /> : <FaBars />}
        </div>
      </header>

      <main className="main-content">
        <Outlet />
      </main>
    </div>
  );
};

export default Dashboard;
