import React, { useState } from 'react';
import { Editor } from 'react-draft-wysiwyg';
import { EditorState, convertFromRaw } from 'draft-js';
import { updateNote, getNoteHistory } from '../utils/noteService';
import * as noteService from '../../utils/noteService';


// import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";

const EditNote = ({ noteId }) => {
  const note = getNoteHistory(noteId);
  const [editorState, setEditorState] = useState(EditorState.createWithContent(convertFromRaw(note.content)));

  const handleSave = () => {
    updateNote(noteId, JSON.stringify(convertFromRaw(editorState.getCurrentContent())));
  };

  return (
    <div>
      <h2>Edit Note</h2>
      <Editor editorState={editorState} onEditorStateChange={setEditorState} />
      <button onClick={handleSave}>Save Changes</button>
    </div>
  );
};

export default EditNote;
