import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const AdminPanel = () => {
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const userRole = localStorage.getItem("role");
    const token = localStorage.getItem("token");

    if (!userRole || userRole !== "admin" || !token) {
      navigate("/login");
      return;
    }

    fetchUsers(token);
    fetchNotes(token);
  }, [navigate]);

  const fetchUsers = async (token) => {
    try {
      const response = await fetch("http://localhost:5000/api/users", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) throw new Error("Failed to fetch users");

      const data = await response.json();
      setUsers(data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const fetchNotes = async (token) => {
    try {
      const response = await fetch("http://localhost:5000/api/notes", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) throw new Error("Failed to fetch notes");

      const data = await response.json();
      setNotes(data);
    } catch (error) {
      console.error("Error fetching notes:", error);
    } finally {
      setLoading(false);
    }
  };

  const changeRole = async (userId) => {
    try {
      const token = localStorage.getItem("token");

      const response = await fetch(`http://localhost:5000/api/promote/${userId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify({ role: "admin" }),
      });

      if (!response.ok) throw new Error("Failed to update role");

      fetchUsers(token);
    } catch (error) {
      console.error("Failed to update role:", error);
    }
  };

  // const deleteUser = async (userId) => {
  //   try {
  //     const token = localStorage.getItem("token");

  //     const response = await fetch(`http://localhost:5000/api/delete/${userId}`, {
  //       method: "DELETE",
  //       headers: { Authorization: `Bearer ${token}` },
  //     });

  //     if (!response.ok) throw new Error("Failed to delete user");

  //     fetchUsers(token);
  //   } catch (error) {
  //     console.error("Failed to delete user:", error);
  //   }
  // };
  const deleteUser = async (userId) => {
    try {
      const token = localStorage.getItem("token");

      if (!token) {
        console.error("‚ùå No token found. User might not be logged in.");
        return;
      }

      console.log(`üîπ Sending DELETE request for user ID: ${userId}`);

      const response = await fetch(`http://localhost:5000/api/users/${userId}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });

      const data = await response.json();
      console.log("üîπ API Response:", data);

      if (!response.ok) {
        throw new Error(data.message || "Failed to delete user");
      }

      console.log("‚úÖ User deleted successfully.");
      fetchUsers(); // Refresh user list
    } catch (error) {
      console.error("‚ùå Failed to delete user:", error);
    }
};


  const deleteNote = async (noteId) => {
    try {
      const token = localStorage.getItem("token");

      const response = await fetch(`http://localhost:5000/api/notes/${noteId}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) throw new Error("Failed to delete note");

      fetchNotes(token);
    } catch (error) {
      console.error("Error deleting note:", error);
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div>
      <h2>Admin Panel</h2>

      {/* User Management */}
      <h3>User Management</h3>
      <table border="1">
        <thead>
          <tr>
            <th>Username</th>
            <th>Email</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td>{user.username}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>
                {user.role !== "admin" && (
                  <button onClick={() => changeRole(user._id)}>Promote to Admin</button>
                )}
                <button onClick={() => deleteUser(user._id)} style={{ marginLeft: "10px" }}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Notes Management */}
      {/* Notes Management */}

  <h3>Notes Management</h3>
  <button onClick={() => navigate("/admin/add-note")} style={{ marginBottom: "10px" }}>
    ‚ûï Add New Note
  </button>

  <table border="1" style={{ width: "100%", borderCollapse: "collapse" }}>
    <thead>
      <tr>
        <th style={{ width: "25%" }}>Title</th>
        <th style={{ width: "50%" }}>Content</th>
        <th style={{ width: "25%" }}>Actions</th>
      </tr>
    </thead>
    <tbody>
      {notes.length === 0 ? (
        <tr>
          <td colSpan="3" style={{ textAlign: "center", padding: "10px" }}>
            No notes available.
          </td>
        </tr>
      ) : (
        notes.map((note) => (
          <tr key={note._id}>
            <td style={{ padding: "8px", fontWeight: "bold" }}>{note.title}</td>
            <td style={{ padding: "8px", wordWrap: "break-word", maxWidth: "400px" }}>
              {note.content}
            </td>
            <td style={{ padding: "8px" }}>
              <button
                onClick={() => navigate(`/admin/edit-note/${note._id}`)}
                style={{ marginRight: "10px" }}
              >
                ‚úèÔ∏è Edit
              </button>
              <button
                onClick={() => deleteNote(note._id)}
                style={{ color: "red", border: "1px solid red", padding: "5px 10px" }}
              >
                üóëÔ∏è Delete
              </button>
            </td>
          </tr>
        ))
      )}
    </tbody>
  </table>
</div>

      
  );
};

export default AdminPanel;
