import React, { useState, useEffect } from "react";
import "./Profile.css";

const Profile = () => {
  // Initialize state with data from localStorage (if available)
  const [user, setUser] = useState({
    // Fetched from registration
    name: localStorage.getItem("username") || "",
    email: localStorage.getItem("email") || "",
    password: localStorage.getItem("password") || "", // Not displayed, just stored
    // User-filled fields
    bio: localStorage.getItem("bio") || "",
    profilePic: localStorage.getItem("profilePic") || "https://via.placeholder.com/150",
    phone: localStorage.getItem("phone") || "",
    location: localStorage.getItem("location") || "",
  });

  // State for editing
  const [editMode, setEditMode] = useState(false);
  const [newName, setNewName] = useState(user.name);
  const [newEmail, setNewEmail] = useState(user.email);
  const [newBio, setNewBio] = useState(user.bio);
  const [newProfilePic, setNewProfilePic] = useState(null);
  const [newPhone, setNewPhone] = useState(user.phone);
  const [newLocation, setNewLocation] = useState(user.location);

  useEffect(() => {
    // Re-fetch user data from localStorage if edit mode was toggled (saved or canceled)
    setUser({
      name: localStorage.getItem("username") || "",
      email: localStorage.getItem("email") || "",
      password: localStorage.getItem("password") || "",
      bio: localStorage.getItem("bio") || "",
      profilePic: localStorage.getItem("profilePic") || "https://via.placeholder.com/150",
      phone: localStorage.getItem("phone") || "",
      location: localStorage.getItem("location") || "",
    });
  }, [editMode]);

  const handleSave = () => {
    // Save new data to localStorage
    localStorage.setItem("username", newName);
    localStorage.setItem("email", newEmail);
    localStorage.setItem("bio", newBio);
    localStorage.setItem("phone", newPhone);
    localStorage.setItem("location", newLocation);

    if (newProfilePic) {
      localStorage.setItem("profilePic", newProfilePic);
    }

    // Update local state
    setUser({
      name: newName,
      email: newEmail,
      password: user.password, // Keep password as is (not shown)
      bio: newBio,
      profilePic: newProfilePic || user.profilePic,
      phone: newPhone,
      location: newLocation,
    });

    // Exit edit mode
    setEditMode(false);
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setNewProfilePic(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="profile-container">
      <div className="profile-card">
        {/* Profile Header */}
        <div className="profile-header">
          <img
            src={newProfilePic || user.profilePic}
            alt="Profile"
            className="profile-pic"
          />

          {editMode ? (
            <input
              type="text"
              value={newName}
              onChange={(e) => setNewName(e.target.value)}
              placeholder="Enter your username"
            />
          ) : (
            <h2>{user.name}</h2>
          )}

          {/* Display the email; you can disable editing if you prefer */}
          {editMode ? (
            <input
              type="email"
              value={newEmail}
              onChange={(e) => setNewEmail(e.target.value)}
              placeholder="Enter your email"
            />
          ) : (
            <p className="email">{user.email}</p>
          )}
        </div>

        {/* Profile Details (phone, location) */}
        <div className="profile-details">
          {editMode ? (
            <>
              <input
                type="text"
                value={newPhone}
                onChange={(e) => setNewPhone(e.target.value)}
                placeholder="Enter your phone number"
              />
              <input
                type="text"
                value={newLocation}
                onChange={(e) => setNewLocation(e.target.value)}
                placeholder="Enter your location"
              />
            </>
          ) : (
            <>
              <p><strong>Phone:</strong> {user.phone}</p>
              <p><strong>Location:</strong> {user.location}</p>
            </>
          )}
        </div>

        {/* Profile Bio */}
        <div className="profile-bio">
          {editMode ? (
            <textarea
              value={newBio}
              onChange={(e) => setNewBio(e.target.value)}
              placeholder="Write something about yourself..."
            />
          ) : (
            <p>{user.bio}</p>
          )}
        </div>

        {/* Image Upload Section (only in edit mode) */}
        {editMode && (
          <div className="upload-section">
            <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
            />
          </div>
        )}

        {/* Edit / Save Buttons */}
        <div className="button-group">
          <button
            className="edit-btn"
            onClick={() => setEditMode(!editMode)}
          >
            {editMode ? "Cancel" : "Edit Profile"}
          </button>

          {editMode && (
            <button
              className="save-btn"
              onClick={handleSave}
            >
              Save Changes
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
