import React, { useState, useEffect } from "react";
import "./Profile.css";

const Profile = () => {
  // Initialize state with data from localStorage (if available)
  const [user, setUser] = useState({
    // Fetched from registration
    name: localStorage.getItem("username") || "",
    email: localStorage.getItem("email") || "",
    password: localStorage.getItem("password") || "", // Stored but not shown
    // User-filled fields
    bio: localStorage.getItem("bio") || "",
    profilePic: localStorage.getItem("profilePic") || "https://via.placeholder.com/150",
    phone: localStorage.getItem("phone") || "",
    location: localStorage.getItem("location") || "",
  });

  // Only allow editing of the fields that the user can fill/update
  const [editMode, setEditMode] = useState(false);
  const [newBio, setNewBio] = useState(user.bio);
  const [newProfilePic, setNewProfilePic] = useState(null);
  const [newPhone, setNewPhone] = useState(user.phone);
  const [newLocation, setNewLocation] = useState(user.location);

  useEffect(() => {
    // Re-fetch user data from localStorage when edit mode is toggled (saved or canceled)
    const updatedUser = {
      name: localStorage.getItem("username") || "",
      email: localStorage.getItem("email") || "",
      password: localStorage.getItem("password") || "",
      bio: localStorage.getItem("bio") || "",
      profilePic: localStorage.getItem("profilePic") || "https://via.placeholder.com/150",
      phone: localStorage.getItem("phone") || "",
      location: localStorage.getItem("location") || "",
    };
    setUser(updatedUser);
    setNewBio(updatedUser.bio);
    setNewPhone(updatedUser.phone);
    setNewLocation(updatedUser.location);
  }, [editMode]);

  const handleSave = () => {
    // Save only the user-updated fields to localStorage
    localStorage.setItem("bio", newBio);
    localStorage.setItem("phone", newPhone);
    localStorage.setItem("location", newLocation);
    if (newProfilePic) {
      localStorage.setItem("profilePic", newProfilePic);
    }

    // Update local state (keeping registration data unchanged)
    setUser({
      ...user,
      bio: newBio,
      profilePic: newProfilePic || user.profilePic,
      phone: newPhone,
      location: newLocation,
    });

    // Exit edit mode
    setEditMode(false);
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setNewProfilePic(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="profile-container">
      <div className="profile-card">
        {/* Profile Header */}
        <div className="profile-header">
          <img
            src={newProfilePic || user.profilePic}
            alt="Profile"
            className="profile-pic"
          />
          <h2>{user.name || "Username not provided"}</h2>
          <p className="email">{user.email || "Email not provided"}</p>
        </div>

        {/* Editable Profile Details (phone, location) */}
        <div className="profile-details">
          {editMode ? (
            <>
              <input
                type="text"
                value={newPhone}
                onChange={(e) => setNewPhone(e.target.value)}
                placeholder="Enter your phone number"
              />
              <input
                type="text"
                value={newLocation}
                onChange={(e) => setNewLocation(e.target.value)}
                placeholder="Enter your location"
              />
            </>
          ) : (
            <>
              <p>
                <strong>Phone:</strong> {user.phone || "Not provided"}
              </p>
              <p>
                <strong>Location:</strong> {user.location || "Not provided"}
              </p>
            </>
          )}
        </div>

        {/* Editable Profile Bio */}
        <div className="profile-bio">
          {editMode ? (
            <textarea
              value={newBio}
              onChange={(e) => setNewBio(e.target.value)}
              placeholder="Write something about yourself..."
            />
          ) : (
            <p>{user.bio || "No bio provided"}</p>
          )}
        </div>

        {/* Image Upload Section (only in edit mode) */}
        {editMode && (
          <div className="upload-section">
            <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
            />
          </div>
        )}

        {/* Edit / Save Buttons */}
        <div className="button-group">
          <button
            className="edit-btn"
            onClick={() => setEditMode(!editMode)}
          >
            {editMode ? "Cancel" : "Edit Profile"}
          </button>
          {editMode && (
            <button className="save-btn" onClick={handleSave}>
              Save Changes
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
