import React, { useState, useEffect } from "react";
import { Editor } from "@tinymce/tinymce-react";
import { useNavigate } from "react-router-dom";

const NoteManager = () => {
  const navigate = useNavigate();
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [note, setNote] = useState({ title: "", content: "" });
  const [editingNoteId, setEditingNoteId] = useState(null);

  useEffect(() => {
    const storedNotes = JSON.parse(localStorage.getItem("notes")) || [];
    setNotes(storedNotes);
    setLoading(false);
  }, []);

  const saveNote = () => {
    const updatedNotes = [...notes];
    const newNote = { ...note, id: Date.now() };

    const cleanedContent = newNote.content.replace(/^<p>/, "").replace(/<\/p>$/, "");
    newNote.content = cleanedContent;
    console.log("Saving content:", newNote.content);

    if (editingNoteId) {
      const index = notes.findIndex((n) => n.id === editingNoteId);
      updatedNotes[index] = newNote;
    } else {
      updatedNotes.unshift(newNote);
    }

    localStorage.setItem("notes", JSON.stringify(updatedNotes));
    setNotes(updatedNotes);
    setNote({ title: "", content: "" });
    setEditingNoteId(null);
  };

  const editNote = (note) => {
    setEditingNoteId(note.id);
    setNote({ title: note.title, content: note.content });
  };

  const deleteNote = (noteId) => {
    const updatedNotes = notes.filter((n) => n.id !== noteId);
    localStorage.setItem("notes", JSON.stringify(updatedNotes));
    setNotes(updatedNotes);
  };

  if (loading) return <p className="text-center text-gray-500">Loading...</p>;

  return (
    <div className="p-8 font-roboto bg-gray-100 rounded-lg shadow-md max-w-4xl mx-auto md:p-5">
      <h2 className="text-3xl font-semibold text-gray-800 mb-5">Note Manager</h2>

      <div className="mb-8 bg-white p-5 rounded-lg shadow-sm">
        <input
          type="text"
          placeholder="Title"
          value={note.title}
          onChange={(e) => setNote({ ...note, title: e.target.value })}
          className="w-full p-4 text-lg border border-gray-300 rounded-md mb-4 bg-gray-50 focus:outline-none focus:border-green-500 focus:ring-2 focus:ring-green-200"
        />
        <Editor
          apiKey="qks55it6scopg5ibx5k982jzxunxu1x79rdlzkmx2yapv5bb"
          value={note.content}
          onEditorChange={(content) => setNote({ ...note, content })}
          init={{
            height: 250,
            menubar: false,
            plugins: "lists link image table code",
            toolbar: "undo redo | bold italic underline | bullist numlist | link | code",
            content_css: "https://cdn.jsdelivr.net/npm/tinymce@5/skins/ui/oxide/content.css",
          }}
        />
        <button
          onClick={saveNote}
          className="mt-4 px-8 py-3 bg-green-500 text-white rounded-md text-base hover:bg-green-600 hover:shadow-md hover:-translate-y-0.5 active:translate-y-0.5 focus:outline-none focus:ring-2 focus:ring-green-400 transition-all duration-200"
        >
          {editingNoteId ? "Update Note" : "Add Note"}
        </button>
      </div>

      <h3 className="text-2xl font-medium text-gray-600 mt-10 mb-4">All Notes</h3>
      <table className="w-full border-collapse border border-gray-300 rounded-md">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-3 text-left text-gray-700 font-semibold">Title</th>
            <th className="p-3 text-left text-gray-700 font-semibold">Content</th>
            <th className="p-3 text-left text-gray-700 font-semibold">Actions</th>
          </tr>
        </thead>
        <tbody>
          {notes.map((note) => (
            <tr key={note.id} className="border-t border-gray-300 hover:bg-gray-50">
              <td className="p-3 text-gray-700">{note.title}</td>
              <td className="p-3 text-gray-600 prose prose-p:mb-2">
                <div dangerouslySetInnerHTML={{ __html: note.content }} />
              </td>
              <td className="p-3">
                <button
                  onClick={() => editNote(note)}
                  className="mr-3 px-4 py-2 bg-green-500 text-white rounded-md text-sm hover:bg-green-600 transition-colors duration-200"
                >
                  Edit
                </button>
                <button
                  onClick={() => deleteNote(note.id)}
                  className="px-4 py-2 bg-red-500 text-white rounded-md text-sm hover:bg-red-600 transition-colors duration-200"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default NoteManager;
