import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Notes = () => {
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch notes from localStorage when the component mounts
  useEffect(() => {
    const fetchNotes = () => {
      const storedNotes = JSON.parse(localStorage.getItem("notes")) || [];
      setNotes(storedNotes);
      setLoading(false);
    };

    fetchNotes();
  }, []);

  // Show loading indicator while fetching data
  if (loading) {
    return <div className="text-white text-lg">Loading notes...</div>;
  }

  return (
    <div className="min-h-screen flex flex-col justify-center items-center bg-gradient-to-r from-blue-900 to-blue-500 py-12 px-6">
      <div className="text-center text-white mb-8">
        <h2 className="text-4xl font-semibold uppercase tracking-widest">
          My Notes
        </h2>
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
        {notes.length === 0 ? (
          <p className="text-white text-lg">No notes available.</p>
        ) : (
          notes.map((note) => (
            <div key={note.id} className="w-72 h-80 group perspective">
              <div className="w-full h-full relative transform-style preserve-3d transition-transform duration-500 group-hover:rotate-y-180">
                {/* Front of the card */}
                <div className="absolute inset-0 bg-gradient-to-r from-blue-700 to-blue-400 text-white flex flex-col justify-center items-center rounded-lg shadow-lg backface-hidden p-4">
                  <h3 className="text-xl font-semibold">{note.title}</h3>
                </div>

                {/* Back of the card */}
                <div className="absolute inset-0 bg-blue-600 text-white flex flex-col justify-center items-center rounded-lg shadow-lg backface-hidden transform rotate-y-180 p-4">
                  <h3 className="text-xl font-semibold">{note.title}</h3>
                  <Link
                    to={`/notes/${note.id}`}
                    className="mt-4 inline-block px-6 py-2 bg-blue-500 text-white text-lg font-semibold rounded-lg shadow-md transition-all transform hover:scale-105 hover:bg-blue-700"
                  >
                    View Note
                  </Link>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Notes;
