import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
import SpeechRecognition, { useSpeechRecognition } from "react-speech-recognition";
import { saveAs } from "file-saver";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";

const AddNote = ({ addNote }) => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [category, setCategory] = useState("");
  const [tags, setTags] = useState("");
  const navigate = useNavigate();
  const quillRef = useRef(null);

  const { transcript, resetTranscript, listening } = useSpeechRecognition();

  const handleAddNote = () => {
    const trimmedTitle = title.trim();
    const trimmedContent = content.trim();

    if (!trimmedTitle || !trimmedContent) {
      alert("Title and content are required and cannot be empty!");
      return;
    }

    const newNote = {
      id: Date.now(),
      title: trimmedTitle,
      content: trimmedContent,
      category: category.trim(),
      tags: tags.split(",").map((tag) => tag.trim()),
      timestamp: new Date().toISOString(),
    };

    addNote(newNote);
    navigate("/notes");
  };

  const startListening = () => SpeechRecognition.startListening({ continuous: true });
  const stopListening = () => SpeechRecognition.stopListening();

  const appendTranscript = () => {
    setContent((prevContent) => prevContent + " " + transcript);
    resetTranscript();
  };

  const exportToPDF = () => {
    const quillEditor = quillRef.current.getEditor().root;

    html2canvas(quillEditor).then((canvas) => {
      const imgData = canvas.toDataURL("image/png");
      const doc = new jsPDF();
      doc.addImage(imgData, "PNG", 10, 10, 180, 0);
      doc.save(`${title}.pdf`);
    });
  };

  return (
    <div>
      <h2>Add New Note</h2>
      <input
        type="text"
        placeholder="Note Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />
      <ReactQuill
        ref={quillRef}
        theme="snow"
        value={content}
        onChange={setContent}
        placeholder="Write your note..."
        className="quill-editor"
      />
      <select value={category} onChange={(e) => setCategory(e.target.value)}>
        <option value="">Select Category</option>
        <option value="Work">Work</option>
        <option value="Personal">Personal</option>
        <option value="Study">Study</option>
      </select>
      <input
        type="text"
        placeholder="Tags (comma-separated)"
        value={tags}
        onChange={(e) => setTags(e.target.value)}
      />

      <button onClick={handleAddNote}>Add Note</button>
      <button onClick={exportToPDF}>Export as PDF</button>

      <div>
        <button onClick={startListening} aria-label="Start Speech-to-Text">
          🎙 Start Speech
        </button>
        <button onClick={stopListening} aria-label="Stop Speech-to-Text">
          ⏹ Stop Speech
        </button>
        <button onClick={resetTranscript} aria-label="Reset Transcript">
          🔄 Reset
        </button>
        <button onClick={appendTranscript}>Add Transcript to Note</button>
        <p>{listening ? "Listening..." : "Click Start to use speech-to-text"}</p>
        <textarea value={transcript} readOnly />
      </div>
    </div>
  );
};

export default AddNote;
