import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const AdminPanel = () => {
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const userRole = localStorage.getItem("role");
    const token = localStorage.getItem("token");

    if (!userRole || userRole !== "admin" || !token) {
      navigate("/login");
      return;
    }

    fetchUsers(token);
    fetchNotes(token);
  }, [navigate]);

  const fetchUsers = async (token) => {
    try {
      const response = await fetch("http://localhost:5000/api/users", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) throw new Error("Failed to fetch users");

      const data = await response.json();
      setUsers(data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const fetchNotes = async (token) => {
    try {
      const response = await fetch("http://localhost:5000/api/notes", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) throw new Error("Failed to fetch notes");

      const data = await response.json();
      setNotes(data);
    } catch (error) {
      console.error("Error fetching notes:", error);
    } finally {
      setLoading(false);
    }
  };

  const changeRole = async (userId) => {
    try {
      const token = localStorage.getItem("token");

      const response = await fetch(`http://localhost:5000/api/promote/${userId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify({ role: "admin" }),
      });

      if (!response.ok) throw new Error("Failed to update role");

      fetchUsers(token);
    } catch (error) {
      console.error("Failed to update role:", error);
    }
  };

  // const deleteUser = async (userId) => {
  //   try {
  //     const token = localStorage.getItem("token");

  //     const response = await fetch(`http://localhost:5000/api/delete/${userId}`, {
  //       method: "DELETE",
  //       headers: { Authorization: `Bearer ${token}` },
  //     });

  //     if (!response.ok) throw new Error("Failed to delete user");

  //     fetchUsers(token);
  //   } catch (error) {
  //     console.error("Failed to delete user:", error);
  //   }
  // };
  const deleteUser = async (userId) => {
    try {
      const token = localStorage.getItem("token");

      if (!token) {
        console.error("❌ No token found. User might not be logged in.");
        return;
      }

      console.log(`🔹 Sending DELETE request for user ID: ${userId}`);

      const response = await fetch(`http://localhost:5000/api/users/${userId}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });

      const data = await response.json();
      console.log("🔹 API Response:", data);

      if (!response.ok) {
        throw new Error(data.message || "Failed to delete user");
      }

      console.log("✅ User deleted successfully.");
      fetchUsers(); // Refresh user list
    } catch (error) {
      console.error("❌ Failed to delete user:", error);
    }
};


  const deleteNote = async (noteId) => {
    try {
      const token = localStorage.getItem("token");

      const response = await fetch(`http://localhost:5000/api/notes/${noteId}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) throw new Error("Failed to delete note");

      fetchNotes(token);
    } catch (error) {
      console.error("Error deleting note:", error);
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div>
      <h2>Admin Panel</h2>

      {/* User Management */}
      <h3>User Management</h3>
      <table border="1">
        <thead>
          <tr>
            <th>Username</th>
            <th>Email</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td>{user.username}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>
                {user.role !== "admin" && (
                  <button onClick={() => changeRole(user._id)}>Promote to Admin</button>
                )}
                <button onClick={() => deleteUser(user._id)} style={{ marginLeft: "10px" }}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Notes Management */}
      <h3>Notes Management</h3>
      <button onClick={() => navigate("/admin/add-note")}>➕ Add New Note</button>
      <table border="1" style={{ marginTop: "10px", width: "100%" }}>
        <thead>
          <tr>
            <th>Title</th>
            <th>Content</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {notes.length === 0 ? (
            <tr>
              <td colSpan="3">No notes available.</td>
            </tr>
          ) : (
            notes.map((note) => (
              <tr key={note._id}>
                <td>{note.title}</td>
                <td>{note.content}</td>
                <td>
                  <button onClick={() => deleteNote(note._id)} style={{ marginLeft: "10px" }}>
                    🗑️ Delete
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default AdminPanel;
