import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { saveAs } from "file-saver";
import jsPDF from "jspdf";
import "./NoteManager.css";

const NoteManager = () => {
  const navigate = useNavigate();
  const [notes, setNotes] = useState([]);
  const [note, setNote] = useState({ title: "", content: "", tags: "" });
  const [editingNoteId, setEditingNoteId] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [darkMode, setDarkMode] = useState(
    JSON.parse(localStorage.getItem("darkMode")) || false
  );

  useEffect(() => {
    const storedNotes = JSON.parse(localStorage.getItem("notes")) || [];
    setNotes(storedNotes);
  }, []);

  useEffect(() => {
    localStorage.setItem("notes", JSON.stringify(notes));
  }, [notes]);

  useEffect(() => {
    localStorage.setItem("darkMode", JSON.stringify(darkMode));
  }, [darkMode]);

  const saveNote = () => {
    if (!note.title.trim() || !note.content.trim()) return;

    const newNote = {
      ...note,
      id: editingNoteId || Date.now(),
      timestamp: new Date().toLocaleString(),
    };

    setNotes((prevNotes) =>
      editingNoteId
        ? prevNotes.map((n) => (n.id === editingNoteId ? newNote : n))
        : [newNote, ...prevNotes]
    );
    setNote({ title: "", content: "", tags: "" });
    setEditingNoteId(null);
  };

  const editNote = (note) => {
    setEditingNoteId(note.id);
    setNote({ title: note.title, content: note.content, tags: note.tags });
  };

  const deleteNote = (noteId) => {
    setNotes(notes.filter((n) => n.id !== noteId));
  };

  const exportToPDF = () => {
    const doc = new jsPDF();
    doc.text("Notes", 10, 10);
    notes.forEach((note, index) => {
      doc.text(`${index + 1}. ${note.title}\n${note.content}\n`, 10, 20 + index * 10);
    });
    doc.save("notes.pdf");
  };

  const filteredNotes = notes.filter(
    (note) =>
      note.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      note.content.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className={`note-manager-container ${darkMode ? "dark-mode" : ""}`}>
      <h2>Note Manager</h2>
      <button onClick={() => setDarkMode(!darkMode)}>Toggle Dark Mode</button>
      <input
        type="text"
        placeholder="Search Notes"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <div className="note-editor">
        <input
          type="text"
          placeholder="Title"
          value={note.title}
          onChange={(e) => setNote({ ...note, title: e.target.value })}
          className="note-title-input"
        />
        <textarea
          placeholder="Content"
          value={note.content}
          onChange={(e) => setNote({ ...note, content: e.target.value })}
          className="note-content-textarea"
        />
        <input
          type="text"
          placeholder="Tags (comma-separated)"
          value={note.tags}
          onChange={(e) => setNote({ ...note, tags: e.target.value })}
        />
        <button onClick={saveNote} className="save-button">
          {editingNoteId ? "Update Note" : "Add Note"}
        </button>
      </div>
      <button onClick={exportToPDF}>Export as PDF</button>
      <h3>All Notes</h3>
      <table className="notes-table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Content</th>
            <th>Tags</th>
            <th>Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredNotes.map((note) => (
            <tr key={note.id}>
              <td>{note.title}</td>
              <td>{note.content}</td>
              <td>{note.tags}</td>
              <td>{note.timestamp}</td>
              <td>
                <button onClick={() => editNote(note)} className="edit-button">
                  Edit
                </button>
                <button
                  onClick={() => deleteNote(note.id)}
                  className="delete-button"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default NoteManager;
