import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const AdminPanel = () => {
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);

  // State for adding/editing a note
  const [note, setNote] = useState({ title: "", subject: "", content: "" });
  const [editingNoteId, setEditingNoteId] = useState(null);

  useEffect(() => {
    const userRole = localStorage.getItem("role");
    const token = localStorage.getItem("token");

    if (!userRole || userRole !== "admin" || !token) {
      navigate("/login");
      return;
    }

    fetchUsers(token);
    fetchNotes(token);
  }, [navigate]);

  // Fetch users
  const fetchUsers = async (token) => {
    try {
      const response = await fetch("http://localhost:5000/api/users", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) throw new Error("Failed to fetch users");

      const data = await response.json();
      setUsers(data);
    } catch (error) {
      console.error("üö® Error fetching users:", error.message);
    }
  };

  // Fetch notes
  const fetchNotes = async (token) => {
    try {
      const response = await fetch("http://localhost:5000/api/notes", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) throw new Error("Failed to fetch notes");

      const data = await response.json();
      setNotes(data);
    } catch (error) {
      console.error("üö® Error fetching notes:", error.message);
    } finally {
      setLoading(false);
    }
  };

  // Create or Update Note
  const saveNote = async () => {
    try {
      const token = localStorage.getItem("token");
      const method = editingNoteId ? "PUT" : "POST";
      const url = editingNoteId
        ? `http://localhost:5000/api/notes/${editingNoteId}`
        : "http://localhost:5000/api/notes";

      const response = await fetch(url, {
        method,
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify(note),
      });

      if (!response.ok) throw new Error("Failed to save note");

      setNote({ title: "", subject: "", content: "" });
      setEditingNoteId(null);
      fetchNotes(token);
    } catch (error) {
      console.error("üö® Error saving note:", error.message);
    }
  };

  // Edit a Note
  const editNote = (note) => {
    setEditingNoteId(note._id);
    setNote({ title: note.title, subject: note.subject, content: note.content });
  };

  // Delete a Note
  const deleteNote = async (noteId) => {
    try {
      const token = localStorage.getItem("token");

      const response = await fetch(`http://localhost:5000/api/notes/${noteId}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) throw new Error("Failed to delete note");

      fetchNotes(token);
    } catch (error) {
      console.error("üö® Error deleting note:", error.message);
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div>
      <h2>Admin Panel</h2>

      {/* User Management */}
      <h3>User Management</h3>
      <table border="1">
        <thead>
          <tr>
            <th>Username</th>
            <th>Email</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td>{user.username}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>
                {user.role !== "admin" && (
                  <button onClick={() => changeRole(user._id)}>Promote to Admin</button>
                )}
                <button onClick={() => deleteUser(user._id)} style={{ marginLeft: "10px" }}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Note Management */}
      <h3>Note Management</h3>
      <div>
        <input
          type="text"
          placeholder="Title"
          value={note.title}
          onChange={(e) => setNote({ ...note, title: e.target.value })}
        />
        <input
          type="text"
          placeholder="Subject"
          value={note.subject}
          onChange={(e) => setNote({ ...note, subject: e.target.value })}
        />
        <textarea
          placeholder="Content"
          value={note.content}
          onChange={(e) => setNote({ ...note, content: e.target.value })}
        />
        <button onClick={saveNote}>{editingNoteId ? "Update Note" : "Add Note"}</button>
      </div>

      {/* Notes List */}
      <h3>All Notes</h3>
      <table border="1">
        <thead>
          <tr>
            <th>Title</th>
            <th>Subject</th>
            <th>Content</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {notes.map((note) => (
            <tr key={note._id}>
              <td>{note.title}</td>
              <td>{note.subject}</td>
              <td>{note.content}</td>
              <td>
                <button onClick={() => editNote(note)}>Edit</button>
                <button onClick={() => deleteNote(note._id)} style={{ marginLeft: "10px" }}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminPanel;
