import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { saveAs } from "file-saver";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";
import "./ManageNotes.css";

const ManageNotes = ({ notes = [], deleteNote, updateNote }) => {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedNotes, setSelectedNotes] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    // Simulate fetching notes
    setTimeout(() => {
      setIsLoading(false);
    }, 1000);
  }, []);

  if (typeof deleteNote !== "function") {
    console.error("deleteNote is not a function. Please pass it as a prop.");
    return <p>Error: deleteNote function is missing!</p>;
  }

  if (typeof updateNote !== "function") {
    console.error("updateNote is not a function. Please pass it as a prop.");
    return <p>Error: updateNote function is missing!</p>;
  }

  const handleDeleteSelected = () => {
    if (selectedNotes.length > 0) {
      const confirmDelete = window.confirm(
        "Are you sure you want to delete the selected notes?"
      );
      if (confirmDelete) {
        selectedNotes.forEach((id) => deleteNote(id));
        setSelectedNotes([]);
      }
    }
  };

  const handleExport = (format) => {
    const filteredNotes = notes.filter((note) =>
      note.title.toLowerCase().includes(searchQuery.toLowerCase())
    );

    if (filteredNotes.length === 0) {
      alert("No notes to export!");
      return;
    }

    if (format === "pdf") {
      const doc = new jsPDF();
      let y = 10;
      filteredNotes.forEach((note, index) => {
        doc.text(`${index + 1}. ${note.title}`, 10, y);
        doc.text(note.content.replace(/<\/?[^>]+(>|$)/g, ""), 10, y + 10);
        y += 20;
      });
      doc.save("notes.pdf");
    } else if (format === "txt") {
      const textContent = filteredNotes
        .map((note) => `${note.title}\n${note.content.replace(/<\/?[^>]+(>|$)/g, "")}\n`)
        .join("\n");
      const blob = new Blob([textContent], { type: "text/plain;charset=utf-8" });
      saveAs(blob, "notes.txt");
    }
  };

  const filteredNotes = notes.filter((note) =>
    note.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  if (isLoading) {
    return <p>Loading notes...</p>;
  }

  return (
    <div className="manage-notes">
      <h2>Manage Notes</h2>

      <input
        type="text"
        placeholder="Search Notes..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        aria-label="Search Notes"
      />

      <button
        onClick={handleDeleteSelected}
        disabled={selectedNotes.length === 0}
        aria-label="Bulk Delete"
      >
        Bulk Delete
      </button>
      <button onClick={() => handleExport("pdf")} aria-label="Export as PDF">
        Export as PDF
      </button>
      <button onClick={() => handleExport("txt")} aria-label="Export as TXT">
        Export as TXT
      </button>

      {filteredNotes.length === 0 ? (
        <p>No notes found.</p>
      ) : (
        <ul>
          {filteredNotes.map((note) => (
            <li key={note.id}>
              <input
                type="checkbox"
                checked={selectedNotes.includes(note.id)}
                onChange={() =>
                  setSelectedNotes((prev) =>
                    prev.includes(note.id)
                      ? prev.filter((id) => id !== note.id)
                      : [...prev, note.id]
                  )
                }
                aria-label={`Select note: ${note.title}`}
              />
              <strong>{note.title}</strong>
              <p dangerouslySetInnerHTML={{ __html: note.content }} />
              <button onClick={() => navigate(`/admin/edit-note/${note.id}`)}>
                Edit
              </button>
              <button onClick={() => deleteNote(note.id)}>Delete</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ManageNotes;
