  const deleteUser = async (userId) => {
    try {
      const token = localStorage.getItem("token");

      if (!token) {
        console.error("‚ùå No token found. User might not be logged in.");
        return;
      }

      console.log(`üîπ Sending DELETE request for user ID: ${userId}`);

      const response = await fetch(`http://localhost:5000/api/users/${userId}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });

      const data = await response.json();
      console.log("üîπ API Response:", data);

      if (!response.ok) {
        throw new Error(data.message || "Failed to delete user");
      }

      console.log("‚úÖ User deleted successfully.");
      fetchUsers(); // Refresh user list
    } catch (error) {
      console.error("‚ùå Failed to delete user:", error);
    }
};


  const deleteNote = async (noteId) => {
    try {
      const token = localStorage.getItem("token");

      const response = await fetch(`http://localhost:5000/api/notes/${noteId}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) throw new Error("Failed to delete note");

      fetchNotes(token);
    } catch (error) {
      console.error("Error deleting note:", error);
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div>
      <h2>Admin Panel</h2>

      {/* User Management */}
      <h3>User Management</h3>
      <table border="1">
        <thead>
          <tr>
            <th>Username</th>
            <th>Email</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td>{user.username}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>
                {user.role !== "admin" && (
                  <button onClick={() => changeRole(user._id)}>Promote to Admin</button>
                )}
                <button onClick={() => deleteUser(user._id)} style={{ marginLeft: "10px" }}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Notes Management */}
      <h3>Notes Management</h3>
      <button onClick={() => navigate("/admin/add-note")}>‚ûï Add New Note</button>
      <table border="1" style={{ marginTop: "10px", width: "100%" }}>
        <thead>
          <tr>
            <th>Title</th>
            <th>Content</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {notes.length === 0 ? (
            <tr>
              <td colSpan="3">No notes available.</td>
            </tr>
          ) : (
            notes.map((note) => (
              <tr key={note._id}>
                <td>{note.title}</td>
                <td>{note.content}</td>
                <td>
                  <button onClick={() => deleteNote(note._id)} style={{ marginLeft: "10px" }}>
                    üóëÔ∏è Delete
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
