import React, { useState, useEffect } from "react";
import "../css/Pro";

const Profile = () => {
  // Initialize state with data from localStorage
  const [user, setUser] = useState({
    name: "",
    email: "",
    bio: localStorage.getItem("bio") || "Turning ideas into reality, one line of code at a time.",
    profilePic: localStorage.getItem("profilePic") || "https://via.placeholder.com/150",
    phone: localStorage.getItem("phone") || "+1234567890",
    location: localStorage.getItem("location") || "New York, USA",
  });

  const [editMode, setEditMode] = useState(false);
  const [newBio, setNewBio] = useState(user.bio);
  const [newProfilePic, setNewProfilePic] = useState(null);
  const [newPhone, setNewPhone] = useState(user.phone);
  const [newLocation, setNewLocation] = useState(user.location);

  // Fetch username and email from localStorage on component mount
  useEffect(() => {
    const username = localStorage.getItem("username") || "John Doe";
    const email = localStorage.getItem("email") || "john.doe@example.com";

    console.log("Fetched email from localStorage:", email); // Debugging line

    setUser((prevUser) => ({
      ...prevUser,
      name: username,
      email: email,
    }));
  }, []); // Empty dependency array ensures this runs only once on mount

  // Save changes to localStorage
  const handleSave = () => {
    localStorage.setItem("bio", newBio);
    localStorage.setItem("phone", newPhone);
    localStorage.setItem("location", newLocation);
    if (newProfilePic) {
      localStorage.setItem("profilePic", newProfilePic);
    }

    setUser({
      ...user,
      bio: newBio,
      profilePic: newProfilePic || user.profilePic,
      phone: newPhone,
      location: newLocation,
    });

    setEditMode(false);
  };

  // Handle image upload
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setNewProfilePic(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="profile-container">
      <div className="profile-card">
        {/* Profile Header */}
        <div className="profile-header">
          <img
            src={newProfilePic || user.profilePic}
            alt="Profile"
            className="profile-pic"
          />
          <h2>{user.name}</h2>
          <p className="email">{user.email}</p>
          <p className="tagline">{user.bio}</p>
        </div>

        {/* Edit Mode Fields */}
        {editMode && (
          <div className="edit-fields">
            <textarea
              value={newBio}
              onChange={(e) => setNewBio(e.target.value)}
              placeholder="Write something about yourself..."
            />
            <input
              type="text"
              value={newPhone}
              onChange={(e) => setNewPhone(e.target.value)}
              placeholder="Enter your phone number"
            />
            <input
              type="text"
              value={newLocation}
              onChange={(e) => setNewLocation(e.target.value)}
              placeholder="Enter your location"
            />
            <div className="upload-section">
              <label htmlFor="profile-pic-upload">Upload Profile Picture</label>
              <input
                id="profile-pic-upload"
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
              />
            </div>
          </div>
        )}

        {/* Buttons */}
        <div className="button-group">
          <button className="edit-btn" onClick={() => setEditMode(!editMode)}>
            {editMode ? "Cancel" : "Edit Profile"}
          </button>
          {editMode && (
            <button className="save-btn" onClick={handleSave}>
              Save Changes
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
