import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Notes = () => {
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchNotes = () => {
      const storedNotes = JSON.parse(localStorage.getItem("notes")) || [];
      console.log("Retrieved notes:", storedNotes);
      setNotes(storedNotes);
      setLoading(false);
    };
    fetchNotes();
  }, []);

  if (loading) {
    return <div className="text-center text-gray-400 mt-10">Loading notes...</div>;
  }

  return (
    <div className="bg-gradient-to-br from-[#0d1b2a] to-[#415a77] p-12 rounded-3xl text-center shadow-2xl mt-10 min-h-screen flex flex-col justify-center text-gray-200 font-roboto">
      <div className="mb-10">
        <h2 className="text-4xl text-gray-200 font-bold uppercase tracking-wide">My Notes</h2>
      </div>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8 mt-8 justify-items-center">
        {notes.length === 0 ? (
          <p className="text-lg text-gray-200 opacity-80 col-span-full">No notes available.</p>
        ) : (
          notes.map((note) => {
            console.log("Rendering note content:", note.content);
            return (
              <div
                key={note.id}
                className="w-72 h-80 perspective-1000 flex justify-center items-center hover:-translate-y-2 transition-transform duration-300"
              >
                <div className="w-full h-full relative transform-style-3d transition-transform duration-500 hover:rotate-y-180">
                  <div className="absolute w-full h-full backface-hidden flex flex-col justify-center items-center p-5 rounded-lg shadow-lg bg-gradient-to-br from-[#1b263b] to-[#778da9] text-white text-xl font-semibold hover:shadow-xl">
                    <h3>{note.title}</h3>
                  </div>
                  <div className="absolute w-full h-full backface-hidden flex flex-col justify-center items-center p-5 rounded-lg shadow-lg bg-[#415a77] rotate-y-180">
                    <h3 className="text-xl font-semibold text-white mb-2">{note.title}</h3>
                    <div
                      className="text-gray-200 prose prose-p:mb-2 max-h-40 overflow-y-auto"
                      dangerouslySetInnerHTML={{ __html: note.content }}
                    />
                    <Link
                      to={`/notes/${note.id}`}
                      className="mt-4 inline-block px-4 py-3 bg-blue-500 text-white text-base font-semibold rounded-lg hover:bg-blue-700 hover:scale-105 transition-all duration-200"
                    >
                      View Note
                    </Link>
                  </div>
                </div>
              </div>
            );
          })
        )}
      </div>
    </div>
  );
};

export default Notes;
