import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import './NoteManager.css'; // Importing the CSS file

const NoteManager = () => {
  const navigate = useNavigate();
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [note, setNote] = useState({ title: "", content: "" });
  const [editingNoteId, setEditingNoteId] = useState(null);

  useEffect(() => {
    const userRole = localStorage.getItem("role");
    const token = localStorage.getItem("token");

    if (!userRole || userRole !== "admin" || !token) {
      navigate("/login");
      return;
    }

    fetchNotes(token);
  }, [navigate]);

  // Fetch all notes
  const fetchNotes = async (token) => {
    try {
      const response = await fetch("http://localhost:5000/api/notes", {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!response.ok) throw new Error("Failed to fetch notes");
      const data = await response.json();
      setNotes(data);
    } catch (error) {
      console.error("Error fetching notes:", error.message);
    } finally {
      setLoading(false);
    }
  };

  // Save or update note
  const saveNote = async () => {
    try {
      const token = localStorage.getItem("token");
      const method = editingNoteId ? "PUT" : "POST";
      const url = editingNoteId
        ? `http://localhost:5000/api/notes/${editingNoteId}`
        : "http://localhost:5000/api/notes";

      const response = await fetch(url, {
        method,
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(note),
      });

      const data = await response.json();
      if (!response.ok) throw new Error(data.message || "Failed to save note");

      fetchNotes(token); // Refresh the notes list
      setNote({ title: "", content: "" });
      setEditingNoteId(null); // Reset editing state
    } catch (error) {
      console.error("Error saving note:", error.message);
    }
  };

  // Edit note
  const editNote = (note) => {
    setEditingNoteId(note._id);
    setNote({ title: note.title, content: note.content });
  };

  // Delete note
  const deleteNote = async (noteId) => {
    try {
      const token = localStorage.getItem("token");

      const response = await fetch(`http://localhost:5000/api/notes/${noteId}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) throw new Error("Failed to delete note");

      fetchNotes(token); // Refresh the notes list
    } catch (error) {
      console.error("Error deleting note:", error.message);
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="note-manager-container">
      <h2>Note Manager</h2>

      {/* Note Editor Form */}
      <div className="note-editor">
        <input
          type="text"
          placeholder="Title"
          value={note.title}
          onChange={(e) => setNote({ ...note, title: e.target.value })}
          className="note-title-input"
        />
        <textarea
          placeholder="Content"
          value={note.content}
          onChange={(e) => setNote({ ...note, content: e.target.value })}
          className="note-content-textarea"
        />
        <button onClick={saveNote} className="save-button">
          {editingNoteId ? "Update Note" : "Add Note"}
        </button>
      </div>

      {/* Notes List */}
      <h3>All Notes</h3>
      <table className="notes-table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Content</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {notes.map((note) => (
            <tr key={note._id}>
              <td>{note.title}</td>
              <td>{note.content}</td>
              <td>
                <button onClick={() => editNote(note)} className="edit-button">
                  Edit
                </button>
                <button
                  onClick={() => deleteNote(note._id)}
                  className="delete-button"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default NoteManager;
