import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./Notes.css";

const Notes = () => {
  const [notes, setNotes] = useState([]);
  const [role, setRole] = useState("");

  useEffect(() => {
    // Fetch role from localStorage
    const storedRole = localStorage.getItem("role")?.trim().toLowerCase();
    setRole(storedRole || "");

    // Load notes
    const storedNotes = JSON.parse(localStorage.getItem("notes")) || [
      { id: 1, title: "Operating System", content: "Study process management, memory allocation, and operating system structures." },
      { id: 2, title: "React Basics", content: "Learn about components & props" },
      { id: 3, title: "JavaScript", content: "Understand closures & promises" },
      { id: 4, title: "Computer Networks", content: "Learn about protocols, IP addressing & routing" },
      { id: 5, title: "Database Management System", content: "Understand relational databases & normalization" },
      { id: 6, title: "SQL", content: "Learn SQL queries, joins, and database management" },
      { id: 7, title: "Linux", content: "Understand Linux commands, shell scripting, and file systems" },
      { id: 8, title: "Git & GitHub", content: "Learn version control using Git and remote repositories on GitHub" },
      { id: 9, title: "AI", content: "Study artificial intelligence concepts and machine learning algorithms" },
      { id: 10, title: "ML", content: "Understand machine learning algorithms and techniques" },
      { id: 11, title: "OOPS", content: "Learn object-oriented programming principles like inheritance, polymorphism, and encapsulation" }
    ];
    
    setNotes(storedNotes);
  }, []);

  return (
    <div className="notes-container">
      <div className="notes-header">
        <h2>My Notes</h2>
        
        {/* Only show Admin Panel link for Admin users */}
        {role === "admin" && <Link to="/admin" className="admin-button">Admin Panel</Link>}
      </div>

      <div className="notes-grid">
        {notes.map((note) => (
          <div key={note.id} className="note-card">
            <div className="note-inner">
              {/* Front Side */}
              <div className="note-front">
                <h3>{note.title}</h3>
              </div>
              
              {/* Back Side - Now Contains the "View Note" Link */}
              <div className="note-back">
                <h3>{note.title}</h3>
                <p>{note.content}</p>
                <Link to={`/notes/${note.id}`} className="note-link">View Note</Link>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Notes;
