// export default NoteManager;
import React, { useState, useEffect } from "react";
import ReactQuill from "react-quill"; // Import Quill editor
import { useNavigate } from "react-router-dom";
import "react-quill/dist/quill.snow.css"; // Import Quill's CSS
import "./NoteManager.css"; // Importing CSS file

const NoteManager = () => {
  const navigate = useNavigate();
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [note, setNote] = useState({ title: "", content: "" });
  const [editingNoteId, setEditingNoteId] = useState(null);

  // Fetch notes from localStorage
  useEffect(() => {
    const storedNotes = JSON.parse(localStorage.getItem("notes")) || [];
    setNotes(storedNotes);
    setLoading(false);
  }, []);

  const modules = {
    toolbar: [
      [{ header: "1" }, { header: "2" }, { font: [] }],
      [{ list: "ordered" }, { list: "bullet" }],
      ["bold", "italic", "underline"],
      [{ align: [] }],
      [{ color: [] }, { background: [] }],
      ["link"],
      ["blockquote", "code-block"],
      ["clean"],
    ],
  };

  // Function to clean content before saving
  const cleanContent = (content) => {
    content = content.trim();
    
    // Remove wrapping <p> tags while keeping inline formatting
    if (content.startsWith("<p>") && content.endsWith("</p>")) {
      content = content.slice(3, -4);
    }

    // Remove empty <p> tags, replace <p> with <br>, and remove artifacts
    return content
      .replace(/<p><br><\/p>/g, "") // Remove empty paragraphs
      .replace(/<\/p><p>/g, "<br>") // Convert paragraphs into line breaks
      .replace(/<span class="ql-cursor">.*?<\/span>/g, ""); // Remove cursor artifacts
  };

  const saveNote = () => {
    if (!note.title.trim() || !note.content.trim()) {
      alert("Title and content cannot be empty!");
      return;
    }

    const updatedNotes = [...notes];
    const newNote = { ...note, id: Date.now(), content: cleanContent(note.content) };

    if (editingNoteId) {
      const index = notes.findIndex((n) => n.id === editingNoteId);
      updatedNotes[index] = newNote;
    } else {
      updatedNotes.unshift(newNote); // Prepend the newly created note
    }

    // Save updated notes in localStorage
    localStorage.setItem("notes", JSON.stringify(updatedNotes));
    setNotes(updatedNotes);
    setNote({ title: "", content: "" });
    setEditingNoteId(null);
  };

  const editNote = (note) => {
    setEditingNoteId(note.id);
    setNote({ title: note.title, content: note.content });
  };

  const deleteNote = (noteId) => {
    const updatedNotes = notes.filter((n) => n.id !== noteId);
    localStorage.setItem("notes", JSON.stringify(updatedNotes)); // Update localStorage
    setNotes(updatedNotes);
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="note-manager-container">
      <h2>Note Manager</h2>

      <div className="note-editor">
        <input
          type="text"
          placeholder="Title"
          value={note.title}
          onChange={(e) => setNote({ ...note, title: e.target.value })}
          className="note-title-input"
        />
        <ReactQuill
          value={note.content}
          onChange={(content) => setNote({ ...note, content })}
          modules={modules}
          className="note-content-quill"
        />
        <button onClick={saveNote} className="save-button">
          {editingNoteId ? "Update Note" : "Add Note"}
        </button>
      </div>

      <h3>All Notes</h3>
      <table className="notes-table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Content</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
  {notes.map((note) => (
    <tr key={note.id}>
      <td>{note.title}</td>
      <td>{note.content}</td> {/* This was treating HTML as plain text */}
      <td>
        <button onClick={() => editNote(note)} className="edit-button">
          Edit
        </button>
        <button onClick={() => deleteNote(note.id)} className="delete-button">
          Delete
        </button>
      </td>
    </tr>
  ))}
</tbody>

      </table>
    </div>
  );
};

export default NoteManager;
