import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Notes = () => {
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchNotes = () => {
      const storedNotes = JSON.parse(localStorage.getItem("notes")) || [];
      setNotes(storedNotes);
      setLoading(false);
    };
    fetchNotes();
  }, []);

  if (loading) {
    return <div className="text-center text-white text-lg">Loading notes...</div>;
  }

  return (
    <div className="bg-gradient-to-br from-[#0d1b2a] to-[#415a77] p-12 rounded-2xl text-center shadow-xl mt-10 min-h-screen flex flex-col justify-center text-[#e0e1dd] font-roboto">
      <h2 className="text-3xl font-bold uppercase tracking-wider mb-10">My Notes</h2>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8 justify-center">
        {notes.length === 0 ? (
          <p className="text-lg opacity-80">No notes available.</p>
        ) : (
          notes.map((note) => (
            <div key={note.id} className="w-72 h-80 perspective relative flex justify-center items-center transition-transform transform hover:-translate-y-2">
              <div className="w-full h-full relative transform-style-3d transition-transform duration-500 hover:rotate-y-180">
                {/* Front Side */}
                <div className="absolute w-full h-full flex flex-col justify-center items-center text-center p-5 rounded-lg shadow-lg bg-gradient-to-br from-[#1b263b] to-[#778da9] text-white font-semibold text-xl backface-hidden">
                  <h3>{note.title}</h3>
                </div>
                {/* Back Side */}
                <div className="absolute w-full h-full flex flex-col justify-center items-center text-center p-5 rounded-lg shadow-lg bg-[#415a77] rotate-y-180">
                  <h3>{note.title}</h3>
                  <Link to={`/notes/${note.id}`} className="mt-4 px-4 py-2 bg-blue-500 text-white text-lg font-semibold rounded-lg transition-transform hover:bg-blue-700 hover:scale-105">View Note</Link>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Notes;
