import React, { useState, useEffect } from "react";
import "./Profile.css";

const Profile = () => {
  const [user, setUser] = useState({
    name: localStorage.getItem("username") || "",
    email: localStorage.getItem("email") || "",
    password: localStorage.getItem("password") || "",
    bio: localStorage.getItem("bio") || "",
    profilePic: localStorage.getItem("profilePic") || "https://via.placeholder.com/150",
    phone: localStorage.getItem("phone") || "",
    location: localStorage.getItem("location") || "",
  });

  const [editMode, setEditMode] = useState(false);
  const [newBio, setNewBio] = useState(user.bio);
  const [newProfilePic, setNewProfilePic] = useState(null);
  const [newPhone, setNewPhone] = useState(user.phone);
  const [newLocation, setNewLocation] = useState(user.location);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    const updatedUser = {
      name: localStorage.getItem("username") || "",
      email: localStorage.getItem("email") || "",
      password: localStorage.getItem("password") || "",
      bio: localStorage.getItem("bio") || "",
      profilePic: localStorage.getItem("profilePic") || "https://via.placeholder.com/150",
      phone: localStorage.getItem("phone") || "",
      location: localStorage.getItem("location") || "",
    };
    setUser(updatedUser);
    setNewBio(updatedUser.bio);
    setNewPhone(updatedUser.phone);
    setNewLocation(updatedUser.location);
  }, [editMode]);

  const validatePhone = (phone) => {
    const phoneRegex = /^\d{10}$/;
    return phoneRegex.test(phone);
  };

  const handleSave = () => {
    if (!validatePhone(newPhone)) {
      alert("Please enter a valid phone number.");
      return;
    }

    localStorage.setItem("bio", newBio);
    localStorage.setItem("phone", newPhone);
    localStorage.setItem("location", newLocation);
    if (newProfilePic) {
      localStorage.setItem("profilePic", newProfilePic);
    }

    setUser({
      ...user,
      bio: newBio,
      profilePic: newProfilePic || user.profilePic,
      phone: newPhone,
      location: newLocation,
    });

    setEditMode(false);
  };

  const handleCancel = () => {
    setNewBio(user.bio);
    setNewPhone(user.phone);
    setNewLocation(user.location);
    setNewProfilePic(null);
    setEditMode(false);
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 5 * 1024 * 1024) {
        alert("File size must be less than 5MB.");
        return;
      }
      if (!file.type.startsWith("image/")) {
        alert("Please upload a valid image file.");
        return;
      }

      const reader = new FileReader();
      reader.onloadend = () => {
        setNewProfilePic(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-header">
          <img
            src={newProfilePic || user.profilePic}
            alt="Profile"
            className="profile-pic"
          />
          <h2>{user.name || "Username not provided"}</h2>
          <p className="email">{user.email || "Email not provided"}</p>
        </div>

        <div className="profile-details">
          {editMode ? (
            <>
              <input
                type="text"
                value={newPhone}
                onChange={(e) => setNewPhone(e.target.value)}
                placeholder="Enter your phone number"
                aria-label="Phone number"
              />
              <input
                type="text"
                value={newLocation}
                onChange={(e) => setNewLocation(e.target.value)}
                placeholder="Enter your location"
                aria-label="Location"
              />
            </>
          ) : (
            <>
              <p>
                <strong>Phone:</strong> {user.phone || "Not provided"}
              </p>
              <p>
                <strong>Location:</strong> {user.location || "Not provided"}
              </p>
            </>
          )}
        </div>

        <div className="profile-bio">
          {editMode ? (
            <textarea
              value={newBio}
              onChange={(e) => setNewBio(e.target.value)}
              placeholder="Write something about yourself..."
              aria-label="Bio"
            />
          ) : (
            <p>{user.bio || "No bio provided"}</p>
          )}
        </div>

        {editMode && (
          <div className="upload-section">
            <label htmlFor="profile-pic-upload">Upload Profile Picture</label>
            <input
              id="profile-pic-upload"
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
            />
          </div>
        )}

        <div className="button-group">
          <button
            className="edit-btn"
            onClick={editMode ? handleCancel : () => setEditMode(true)}
          >
            {editMode ? "Cancel" : "Edit Profile"}
          </button>
          {editMode && (
            <button className="save-btn" onClick={handleSave} disabled={isLoading}>
              {isLoading ? "Saving..." : "Save Changes"}
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
