import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
import "./EditNote.css";

const EditNote = () => {
  const { noteId } = useParams();
  const navigate = useNavigate();
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");

  useEffect(() => {
    const savedNotes = JSON.parse(localStorage.getItem("notes")) || [];
    const note = savedNotes.find((note) => note.id === parseInt(noteId));

    if (note) {
      setTitle(note.title);
      setContent(note.content);
    } else {
      alert("Note not found!");
      navigate("/admin");
    }
  }, [noteId, navigate]);

  const handleSubmit = (e) => {
    e.preventDefault();

    const trimmedTitle = title.trim();
    const trimmedContent = content.trim();

    if (!trimmedTitle || !trimmedContent) {
      alert("Title and content are required and cannot be empty!");
      return;
    }

    const savedNotes = JSON.parse(localStorage.getItem("notes")) || [];
    const updatedNotes = savedNotes.map((note) =>
      note.id === parseInt(noteId) ? { ...note, title: trimmedTitle, content: trimmedContent } : note
    );

    localStorage.setItem("notes", JSON.stringify(updatedNotes));
    navigate("/admin");
  };

  return (
    <div className="edit-note">
      <h2>Edit Note</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="title">Title</label>
        <input
          type="text"
          id="title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
          aria-label="Note Title"
        />
        <label htmlFor="content">Content</label>
        <ReactQuill
          theme="snow"
          value={content}
          onChange={setContent}
          placeholder="Write your note..."
          className="quill-editor"
          aria-label="Note Content"
        />
        <button type="submit" aria-label="Update Note">
          Update Note
        </button>
      </form>
    </div>
  );
};

export default EditNote;
