import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const ProfileCreation = () => {
  const navigate = useNavigate();
  const [profileData, setProfileData] = useState({
    fullName: '',
    bio: '',
  });

  const handleChange = (e) => {
    setProfileData({
      ...profileData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Simulate profile save, you can extend this to save data in the backend
    localStorage.setItem('profile', JSON.stringify(profileData));  // Save profile to localStorage
    navigate('/');  // Navigate back to the dashboard after profile creation
  };

  return (
    <div className="profile-creation-container">
      <h2>Create Your Profile</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <input
            type="text"
            name="fullName"
            value={profileData.fullName}
            onChange={handleChange}
            placeholder="Full Name"
            required
          />
        </div>
        <div>
          <textarea
            name="bio"
            value={profileData.bio}
            onChange={handleChange}
            placeholder="Bio"
            required
          ></textarea>
        </div>
        <button type="submit">Create Profile</button>
      </form>
    </div>
  );
};

export default ProfileCreation;
