import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./Notes.css";

const Notes = () => {
  const [notes, setNotes] = useState([]);
  const [role, setRole] = useState(""); // Store user role

  useEffect(() => {
    // Retrieve and set role
    const storedRole = localStorage.getItem("role");
    console.log("Stored Role:", storedRole); // Debugging
    setRole(storedRole || "");

    // Fetch notes
    const storedNotes = JSON.parse(localStorage.getItem("notes")) || [...defaultNotes];
    setNotes(storedNotes);
  }, []);

  return (
    <div className="notes-container">
      <div className="notes-header">
        <h2>My Notes</h2>

        {/* Only show Admin Panel if user is an admin */}
        {role === "admin" && <Link to="/admin" className="admin-button">Admin Panel</Link>}
      </div>

      <div className="notes-grid">
        {notes.map((note) => (
          <div key={note.id} className="note-card">
            <div className="note-inner">
              <div className="note-front">
                <h3>{note.title}</h3>
              </div>
              <div className="note-back">
                <h3>{note.title}</h3>
                <p>{note.content}</p>
                <Link to={`/notes/${note.id}`} className="note-link">View Note</Link>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Notes;
