import React from "react";
import "../css/Home.css";
import FAQ from "./FAQ";

const Home = () => {
  return (
    <div className="home-container flex flex-col items-center justify-center min-h-screen bg-gray-100 p-6">
      {/* Main Card */}
      <div className="bg-white shadow-lg rounded-2xl p-8 w-full max-w-3xl text-center border border-gray-200 animate-fadeIn">
        
        {/* Heading */}
        <h2 className="text-3xl font-bold text-gray-800 mb-4">
          Welcome to Your Dashboard ðŸš€
        </h2>
        <p className="text-gray-600 text-lg">
          Your central hub for managing notes and user information.
        </p>

        {/* Image Section */}
        <div className="mt-6">
        <img 
  src="https://images.unsplash.com/photo-1593642532842-98d0fd5ebc1a?w=800&q=80" 
  alt="Dashboard" 
  className="rounded-lg shadow-md w-full"
/>

        </div>

        {/* Call to Action Button */}
        <div className="mt-6">
          <button className="bg-blue-600 text-white font-semibold py-2 px-6 rounded-lg shadow-md hover:bg-blue-700 transition-all duration-300">
            Get Started
          </button>
        </div>

        {/* FAQ Section */}
        <div className="mt-8">
          <FAQ />
        </div>
      </div>
    </div>
  );
};

export default Home;
