import React, { useEffect, useState, useMemo } from "react";
import { Link } from "react-router-dom";
import "./Notes.css";

const Notes = () => {
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchNotes = () => {
      const storedNotes = JSON.parse(localStorage.getItem("notes")) || [];
      setNotes(storedNotes);
      setLoading(false);
    };

    fetchNotes();
  }, []);

  // Function to clean HTML and preserve line breaks
  const stripHtml = (html = "") => {
    return html
      .replace(/<br\s*\/?>/g, "\n") // Replace <br> tags with newline
      .replace(/<\/p>/g, "\n") // Replace </p> with newline
      .replace(/<\/?[^>]+(>|$)/g, "") // Remove remaining HTML tags
      .trim();
  };

  // Memoized Notes to avoid unnecessary re-renders
  const memoizedNotes = useMemo(() => notes, [notes]);

  if (loading) {
    return <div className="loading">Loading notes...</div>;
  }

  return (
    <div className="notes-container">
      <div className="notes-header">
        <h2>My Notes</h2>
      </div>

      <div className="notes-grid">
        {memoizedNotes.length === 0 ? (
          <p>No notes available.</p>
        ) : (
          memoizedNotes.map((note) => (
            <div key={note.id} className="note-card">
              <div className="note-inner">
                <div className="note-front">
                  <h3>{note.title || "Untitled Note"}</h3>
                </div>

                <div className="note-back">
                  <h3>{note.title || "Untitled Note"}</h3>
                  <p>{stripHtml(note.content) || "No content available."}</p>
                  <Link to={`/notes/${note.id}`} className="note-link">
                    View Note
                  </Link>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Notes;
