import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Editor } from "@ckeditor/ckeditor5-react"; // Import CKEditor
import ClassicEditor from "@ckeditor/ckeditor5-build-classic"; // Import CKEditor build
import "../css/NoteManager.css"; // Import CSS file

const NoteManager = () => {
  const navigate = useNavigate();
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [note, setNote] = useState({ title: "", content: "" });
  const [editingNoteId, setEditingNoteId] = useState(null);

  // Fetch notes from localStorage
  useEffect(() => {
    const storedNotes = JSON.parse(localStorage.getItem("notes")) || [];
    setNotes(storedNotes);
    setLoading(false);
  }, []);

  const saveNote = () => {
    const updatedNotes = [...notes];
    const newNote = { ...note, id: Date.now() };

    // Ensure content doesn't have unnecessary wrapping <p> tags
    const cleanedContent = newNote.content.replace(/^<p>/, "").replace(/<\/p>$/, "");
    newNote.content = cleanedContent;
    console.log("Saving content:", newNote.content);

    if (editingNoteId) {
      const index = notes.findIndex((n) => n.id === editingNoteId);
      updatedNotes[index] = newNote;
    } else {
      updatedNotes.unshift(newNote);
    }

    localStorage.setItem("notes", JSON.stringify(updatedNotes));
    setNotes(updatedNotes);

    setNote({ title: "", content: "" });
    setEditingNoteId(null);
  };

  const editNote = (note) => {
    setEditingNoteId(note.id);
    setNote({ title: note.title, content: note.content });
  };

  const deleteNote = (noteId) => {
    const updatedNotes = notes.filter((n) => n.id !== noteId);
    localStorage.setItem("notes", JSON.stringify(updatedNotes));
    setNotes(updatedNotes);
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="note-manager-container">
      <h2>Note Manager</h2>

      <div className="note-editor">
        <input
          type="text"
          placeholder="Title"
          value={note.title}
          onChange={(e) => setNote({ ...note, title: e.target.value })}
          className="note-title-input"
        />
        {/* CKEditor */}
        <Editor
          editor={ClassicEditor} // Use ClassicEditor build
          data={note.content} // Set initial content
          onChange={(event, editor) => {
            const data = editor.getData(); // Get content from editor
            setNote({ ...note, content: data });
          }}
          config={{
            toolbar: [
              "undo", "redo", "|", "bold", "italic", "underline", "|",
              "bulletedList", "numberedList", "|", "link", "|", "code"
            ],
          }}
        />
        <button onClick={saveNote} className="save-button">
          {editingNoteId ? "Update Note" : "Add Note"}
        </button>
      </div>

      <h3>All Notes</h3>
      <table className="notes-table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Content</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {notes.map((note) => (
            <tr key={note.id}>
              <td>{note.title}</td>
              <td dangerouslySetInnerHTML={{ __html: note.content }}></td>
              <td>
                <button onClick={() => editNote(note)} className="edit-button">
                  Edit
                </button>
                <button
                  onClick={() => deleteNote(note.id)}
                  className="delete-button"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default NoteManager;
