import React, { useEffect, useState } from "react";
import { Link, Navigate } from "react-router-dom";
import { useAuth } from "../../context/AuthContext";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const { user } = useAuth();
  const [noteCount, setNoteCount] = useState(0);
  const [recentActivity, setRecentActivity] = useState([]);
  const [users, setUsers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (user && user.role === "admin") {
      setIsLoading(true);
      setError(null);

      Promise.all([
        fetch("/api/notes/count").then((response) => response.json()),
        fetch("/api/activity").then((response) => response.json()),
        fetch("/api/admin/users").then((response) => response.json()),
      ])
        .then(([noteData, activityData, userData]) => {
          setNoteCount(noteData.count);
          setRecentActivity(activityData);
          setUsers(userData.users);
        })
        .catch((error) => {
          console.error("Error fetching data:", error);
          setError("Failed to fetch data. Please try again later.");
        })
        .finally(() => setIsLoading(false));
    }
  }, [user]);

  const promoteToAdmin = async (userId) => {
    try {
      const response = await fetch(`/api/admin/users/promote/${userId}`, {
        method: "PUT",
      });
      if (response.ok) {
        // Refresh the user list
        fetch("/api/admin/users")
          .then((response) => response.json())
          .then((data) => setUsers(data.users));
      } else {
        alert("Failed to promote user.");
      }
    } catch (error) {
      console.error("Error promoting user:", error);
    }
  };

  const demoteToUser = async (userId) => {
    try {
      const response = await fetch(`/api/admin/users/demote/${userId}`, {
        method: "PUT",
      });
      if (response.ok) {
        // Refresh the user list
        fetch("/api/admin/users")
          .then((response) => response.json())
          .then((data) => setUsers(data.users));
      } else {
        alert("Failed to demote user.");
      }
    } catch (error) {
      console.error("Error demoting user:", error);
    }
  };

  if (!user || user.role !== "admin") {
    return <Navigate to="/" />;
  }

  if (isLoading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div className="admin-dashboard">
      <h2>Admin Dashboard</h2>
      <nav aria-label="Admin Navigation">
        <ul>
          <li>
            <Link to="/admin/manage-notes" aria-label="Manage Notes">
              Manage Notes
            </Link>
          </li>
          <li>
            <Link to="/admin/add-note" aria-label="Add New Note">
              Add New Note
            </Link>
          </li>
        </ul>
      </nav>

      <section>
        <h3>Quick Actions</h3>
        <button onClick={() => alert("Export all notes")}>Export All Notes</button>
        <button onClick={() => alert("View user activity")}>View User Activity</button>
      </section>

      <section>
        <h3>Statistics</h3>
        <p>Total Notes: {noteCount}</p>
      </section>

      <section>
        <h3>Recent Activity</h3>
        <ul>
          {recentActivity.map((activity, index) => (
            <li key={index}>{activity}</li>
          ))}
        </ul>
      </section>

      <section>
        <h3>User Management</h3>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Role</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user._id}>
                <td>{user.username}</td>
                <td>{user.email}</td>
                <td>{user.role}</td>
                <td>
                  {user.role === "user" && (
                    <button onClick={() => promoteToAdmin(user._id)}>
                      Promote to Admin
                    </button>
                  )}
                  {user.role === "admin" && (
                    <button onClick={() => demoteToUser(user._id)}>
                      Demote to User
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </div>
  );
};

export default AdminDashboard;
