import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './Login.css';
const handleLogin = (username, email) => {
  localStorage.setItem("username", username);
  localStorage.setItem("email", email); // Ensure this line is present
  // Redirect to profile or dashboard
};
const Login = ({ setUsername }) => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [showForm, setShowForm] = useState(true); // State to control form visibility

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const response = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        localStorage.setItem('token', data.token);
        localStorage.setItem('username', data.username); // ✅ Store username
        setUsername(data.username); // ✅ Update state

        setMessage('Login successful! Redirecting...');

        setTimeout(() => {
          navigate('/');
          window.location.reload(); // Refresh UI
        }, 1500);
      } else {
        setMessage(data.message || 'Invalid credentials. Try again.');
      }
    } catch (error) {
      setMessage('Server error. Please try again later.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleCloseForm = () => {
    setShowForm(false); // Close the form when the button is clicked
  };

  return (
    <>
      {showForm && (
        <div className="login-form-container">
          <button className="close-btn" onClick={handleCloseForm}>
            &times; {/* Cross symbol for close */}
          </button>
          <h2>Login</h2>
          <form onSubmit={handleSubmit} className="login-form">
            <div className="input-group">
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
            <div className="input-group">
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleChange}
                required
              />
            </div>
            <button type="submit" className="submit-btn" disabled={isLoading}>
              {isLoading ? 'Logging in...' : 'Login'}
            </button>
          </form>
          {message && <p className={message.includes('success') ? 'success' : 'error'}>{message}</p>}
          <p>Don't have an account? <Link to="/register">Sign Up</Link></p>
        </div>
      )}
    </>
  );
};

export default Login;
