import React from "react";
import "../css/Home.css";
import FAQ from "./FAQ";

const Home = () => {
  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-r from-blue-500 to-purple-600 p-6">
      {/* Card Container */}
      <div className="bg-white/20 backdrop-blur-lg shadow-xl rounded-3xl p-8 w-full max-w-3xl text-center border border-white/30 animate-fadeIn">
        
        {/* Heading */}
        <h2 className="text-4xl font-bold text-white drop-shadow-lg">
          Welcome to Your Dashboard ðŸš€
        </h2>
        <p className="text-gray-200 text-lg mt-2">
          Your central hub for managing notes and user information with ease.
        </p>

        {/* Image Section */}
        <div className="mt-6">
          <img 
            src="https://images.unsplash.com/photo-1593642634367-d91a135587b5?w=800&q=80" 
            alt="Dashboard" 
            className="rounded-lg shadow-lg w-full hover:scale-105 transition-transform duration-300"
          />
        </div>

        {/* Call to Action Button */}
        <div className="mt-6">
          <button className="bg-white/30 text-white font-semibold py-2 px-6 rounded-lg shadow-md backdrop-blur-md hover:bg-white/50 transition-all duration-300">
            Get Started
          </button>
        </div>

        {/* FAQ Section */}
        <div className="mt-8">
          <FAQ />
        </div>
      </div>
    </div>
  );
};

export default Home;
