import React, { useState, useEffect } from "react";
import "./Profile.css";

const Profile = () => {
  const [user, setUser] = useState({
    name: localStorage.getItem("username") || "User",
    email: localStorage.getItem("email") || "user@example.com",
    password: localStorage.getItem("password") || "", // Store password, but don't display it
    bio: localStorage.getItem("bio") || "Write something about yourself...",
    profilePic: localStorage.getItem("profilePic") || "https://via.placeholder.com/150",
    phone: localStorage.getItem("phone") || "123-456-7890",
    location: localStorage.getItem("location") || "Earth",
  });

  const [editMode, setEditMode] = useState(false);
  const [newName, setNewName] = useState(user.name);
  const [newEmail, setNewEmail] = useState(user.email);
  const [newBio, setNewBio] = useState(user.bio);
  const [newProfilePic, setNewProfilePic] = useState(null);
  const [newPhone, setNewPhone] = useState(user.phone);
  const [newLocation, setNewLocation] = useState(user.location);

  useEffect(() => {
    setUser({
      name: localStorage.getItem("username") || "User",
      email: localStorage.getItem("email") || "user@example.com",
      password: localStorage.getItem("password") || "",
      bio: localStorage.getItem("bio") || "Write something about yourself...",
      profilePic: localStorage.getItem("profilePic") || "https://via.placeholder.com/150",
      phone: localStorage.getItem("phone") || "123-456-7890",
      location: localStorage.getItem("location") || "Earth",
    });
  }, [editMode]);

  const handleSave = () => {
    // Save edited data to localStorage
    localStorage.setItem("username", newName);
    localStorage.setItem("email", newEmail);
    localStorage.setItem("bio", newBio);
    localStorage.setItem("phone", newPhone);
    localStorage.setItem("location", newLocation);
    if (newProfilePic) {
      localStorage.setItem("profilePic", newProfilePic);
    }

    setUser({
      name: newName,
      email: newEmail,
      bio: newBio,
      profilePic: newProfilePic || user.profilePic,
      phone: newPhone,
      location: newLocation,
    });

    setEditMode(false);
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setNewProfilePic(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-header">
          <img src={newProfilePic || user.profilePic} alt="Profile" className="profile-pic" />
          {editMode ? (
            <input type="text" value={newName} onChange={(e) => setNewName(e.target.value)} />
          ) : (
            <h2>{user.name}</h2>
          )}
          <p className="email">{user.email}</p>
        </div>

        <div className="profile-details">
          {editMode ? (
            <>
              <input
                type="email"
                value={newEmail}
                onChange={(e) => setNewEmail(e.target.value)}
                placeholder="Email"
                disabled // Email can't be changed after registration
              />
              <input
                type="text"
                value={newPhone}
                onChange={(e) => setNewPhone(e.target.value)}
                placeholder="Phone Number"
              />
              <input
                type="text"
                value={newLocation}
                onChange={(e) => setNewLocation(e.target.value)}
                placeholder="Location"
              />
            </>
          ) : (
            <>
              <p><strong>Email:</strong> {user.email}</p>
              <p><strong>Phone:</strong> {user.phone}</p>
              <p><strong>Location:</strong> {user.location}</p>
            </>
          )}
        </div>

        <div className="profile-bio">
          {editMode ? (
            <textarea value={newBio} onChange={(e) => setNewBio(e.target.value)} />
          ) : (
            <p>{user.bio}</p>
          )}
        </div>

        {editMode && (
          <div className="upload-section">
            <input type="file" accept="image/*" onChange={handleImageUpload} />
          </div>
        )}

        <div className="button-group">
          <button className="edit-btn" onClick={() => setEditMode(!editMode)}>
            {editMode ? "Cancel" : "Edit Profile"}
          </button>

          {editMode && (
            <button className="save-btn" onClick={handleSave}>
              Save Changes
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
