import React, { useState, useEffect } from "react";
import ReactQuill from "react-quill"; // Importing Quill editor
import { useNavigate } from "react-router-dom";
import "react-quill/dist/quill.snow.css"; // Import Quill's CSS
import './NoteManager.css'; // Importing the CSS file

const NoteManager = () => {
  const navigate = useNavigate();
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [note, setNote] = useState({ title: "", content: "" });
  const [editingNoteId, setEditingNoteId] = useState(null);

  // Fetch notes from localStorage
  useEffect(() => {
    const storedNotes = JSON.parse(localStorage.getItem("notes")) || [];
    setNotes(storedNotes);
    setLoading(false);
  }, []);

  // Quill modules configuration with custom image handler
  const modules = {
    toolbar: [
      [{ header: "1" }, { header: "2" }, { font: [] }],
      [{ list: "ordered" }, { list: "bullet" }],
      ["bold", "italic", "underline"],
      [{ align: [] }],
      [{ color: [] }, { background: [] }],
      ["link"],
      ["blockquote", "code-block"],
      ["clean"],
      ["image"], // Add image button to the toolbar
    ],
  };

  // Custom image handler to handle image upload
  const imageHandler = () => {
    const input = document.createElement("input");
    input.setAttribute("type", "file");
    input.setAttribute("accept", "image/*");

    input.click();

    input.onchange = async () => {
      const file = input.files[0];
      const reader = new FileReader();

      reader.onloadend = () => {
        const range = quillRef.current.getEditor().getSelection();
        quillRef.current.getEditor().insertEmbed(range.index, "image", reader.result);
      };

      if (file) {
        reader.readAsDataURL(file); // Convert image file to base64 URL
      }
    };
  };

  // Save note (with image handling)
  const saveNote = () => {
    const updatedNotes = [...notes];
    const newNote = { ...note, id: Date.now() };

    // Clean content by removing unwanted <p> tags
    const cleanedContent = note.content.replace(/<p>/g, '').replace(/<\/p>/g, '');
    newNote.content = cleanedContent;

    if (editingNoteId) {
      const index = notes.findIndex((n) => n.id === editingNoteId);
      updatedNotes[index] = newNote;
    } else {
      updatedNotes.unshift(newNote); // Prepend the newly created note
    }

    // Save updated notes in localStorage
    localStorage.setItem("notes", JSON.stringify(updatedNotes));
    setNotes(updatedNotes);

    setNote({ title: "", content: "" });
    setEditingNoteId(null);
  };

  // Edit a note
  const editNote = (note) => {
    setEditingNoteId(note.id);
    setNote({ title: note.title, content: note.content });
  };

  // Delete a note
  const deleteNote = (noteId) => {
    const updatedNotes = notes.filter((n) => n.id !== noteId);
    localStorage.setItem("notes", JSON.stringify(updatedNotes)); // Update localStorage
    setNotes(updatedNotes);
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="note-manager-container">
      <h2>Note Manager</h2>

      <div className="note-editor">
        <input
          type="text"
          placeholder="Title"
          value={note.title}
          onChange={(e) => setNote({ ...note, title: e.target.value })}
          className="note-title-input"
        />
        <ReactQuill
          ref={quillRef} // Save reference for image handler
          value={note.content}
          onChange={(content) => setNote({ ...note, content })}
          modules={{ ...modules, toolbar: { ...modules.toolbar, image: imageHandler } }}
          className="note-content-quill"
        />
        <button onClick={saveNote} className="save-button">
          {editingNoteId ? "Update Note" : "Add Note"}
        </button>
      </div>

      <h3>All Notes</h3>
      <table className="notes-table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Content</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {notes.map((note) => (
            <tr key={note.id}>
              <td>{note.title}</td>
              <td>{note.content}</td>
              <td>
                <button onClick={() => editNote(note)} className="edit-button">
                  Edit
                </button>
                <button
                  onClick={() => deleteNote(note.id)}
                  className="delete-button"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default NoteManager;
