import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import "../css/Register.css";

const Register = ({ setUsername, setEmail }) => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ username: "", email: "", password: "" });
  const [message, setMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const response = await fetch("http://localhost:5000/api/auth/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      console.log("Registration Response:", data); // Debugging log

      if (response.ok) {
        // Use response email if available; otherwise, fallback to the form email.
        const registeredEmail = data.email || formData.email;

        localStorage.setItem("username", data.username);
        localStorage.setItem("email", registeredEmail);

        if (setUsername) setUsername(data.username);
        if (setEmail) setEmail(registeredEmail);

        setMessage("Registration successful! Redirecting...");

        setTimeout(() => {
          navigate("/");
          // Consider removing window.location.reload() to avoid full page refresh
          window.location.reload();
        }, 1500);
      } else {
        setMessage(data.message || "Registration failed. Try again.");
      }
    } catch (error) {
      console.error("Registration Error:", error);
      setMessage("Server error. Please try again later.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleClose = () => {
    navigate("/");
  };

  return (
    <div className="register-form-container">
      <button className="close-btn" onClick={handleClose}>X</button>
      <h2>Register</h2>
      <form onSubmit={handleSubmit} className="register-form">
        <div className="input-group">
          <input
            type="text"
            name="username"
            placeholder="Username"
            value={formData.username}
            onChange={handleChange}
            required
          />
        </div>
        <div className="input-group">
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div className="input-group">
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="submit-btn" disabled={isLoading}>
          {isLoading ? "Registering..." : "Register"}
        </button>
      </form>
      {message && <p className={message.includes("success") ? "success" : "error"}>{message}</p>}
      <p>Already have an account? <Link to="/login">Login</Link></p>
    </div>
  );
};

export default Register;
