import React, { useState } from 'react';
import { getNotes, deleteNotes } from '../utils/noteService';



const ManageNotes = () => {
  const [notes, setNotes] = useState(getNotes());
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedNotes, setSelectedNotes] = useState([]);

  const handleDelete = () => {
    deleteNotes(selectedNotes);
    setNotes(getNotes());
  };

  return (
    <div>
      <h2>Manage Notes</h2>
      <input type="text" placeholder="Search..." value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} />
      <button onClick={handleDelete} disabled={!selectedNotes.length}>Delete Selected</button>
      <ul>
        {notes.filter(note => note.title.includes(searchQuery)).map(note => (
          <li key={note.id}>
            <input type="checkbox" onChange={() => setSelectedNotes([...selectedNotes, note.id])} />
            {note.title}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageNotes;
