import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
import SpeechRecognition, { useSpeechRecognition } from "react-speech-recognition";
// import { saveAs } from "file-saver";
import jsPDF from "jspdf";

const AddNote = ({ addNote }) => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [category, setCategory] = useState("");
  const [tags, setTags] = useState("");
  const navigate = useNavigate();
  const quillRef = useRef(null);

  const { transcript, resetTranscript, listening } = useSpeechRecognition();

  const handleAddNote = () => {
    if (!title || !content) {
      alert("Title and content are required!");
      return;
    }

    const newNote = {
      id: Date.now(),
      title,
      content,
      category,
      tags: tags.split(",").map(tag => tag.trim()),
      timestamp: new Date().toISOString(),
    };

    addNote(newNote);
    navigate("/admin/manage-notes");
  };

  const startListening = () => SpeechRecognition.startListening({ continuous: true });
  const stopListening = () => SpeechRecognition.stopListening();

  const exportToPDF = () => {
    const doc = new jsPDF();
    doc.text(title, 10, 10);
    doc.text(content.replace(/<\/?[^>]+(>|$)/g, ""), 10, 20); // Remove HTML tags
    doc.save(`${title}.pdf`);
  };

  return (
    <div>
      <h2>Add New Note</h2>
      <input
        type="text"
        placeholder="Note Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />
      <ReactQuill
        ref={quillRef}
        theme="snow"
        value={content}
        onChange={setContent}
        placeholder="Write your note..."
      />
      <input
        type="text"
        placeholder="Category"
        value={category}
        onChange={(e) => setCategory(e.target.value)}
      />
      <input
        type="text"
        placeholder="Tags (comma-separated)"
        value={tags}
        onChange={(e) => setTags(e.target.value)}
      />

      <button onClick={handleAddNote}>Add Note</button>
      <button onClick={exportToPDF}>Export as PDF</button>

      <div>
        <button onClick={startListening}>üéô Start Speech</button>
        <button onClick={stopListening}>‚èπ Stop Speech</button>
        <button onClick={resetTranscript}>üîÑ Reset</button>
        <p>{listening ? "Listening..." : "Click Start to use speech-to-text"}</p>
        <textarea value={transcript} readOnly />
      </div>
    </div>
  );
};

export default AddNote;
