import React, { useState, useEffect } from "react";
import "./Profile.css";

const Profile = () => {
  // Initialize state with data from localStorage
  const [user, setUser] = useState({
    name: localStorage.getItem("username") || "John Doe",
    email: localStorage.getItem("email") || "john.doe@example.com",
    password: localStorage.getItem("password") || "",
    bio: localStorage.getItem("bio") || "Turning ideas into reality, one line of code at a time.",
    profilePic: localStorage.getItem("profilePic") || "https://via.placeholder.com/150",
    phone: localStorage.getItem("phone") || "+1234567890",
    location: localStorage.getItem("location") || "New York, USA",
    skills: JSON.parse(localStorage.getItem("skills")) || [
      { name: "HTML", progress: 90 },
      { name: "CSS", progress: 85 },
      { name: "JavaScript", progress: 80 },
    ],
  });

  const [editMode, setEditMode] = useState(false);
  const [newBio, setNewBio] = useState(user.bio);
  const [newProfilePic, setNewProfilePic] = useState(null);
  const [newPhone, setNewPhone] = useState(user.phone);
  const [newLocation, setNewLocation] = useState(user.location);
  const [theme, setTheme] = useState("light");

  // Toggle theme (light/dark mode)
  const toggleTheme = () => {
    setTheme(theme === "light" ? "dark" : "light");
  };

  // Save changes to localStorage
  const handleSave = () => {
    localStorage.setItem("bio", newBio);
    localStorage.setItem("phone", newPhone);
    localStorage.setItem("location", newLocation);
    if (newProfilePic) {
      localStorage.setItem("profilePic", newProfilePic);
    }

    setUser({
      ...user,
      bio: newBio,
      profilePic: newProfilePic || user.profilePic,
      phone: newPhone,
      location: newLocation,
    });

    setEditMode(false);
  };

  // Handle image upload
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 5 * 1024 * 1024) {
        alert("File size must be less than 5MB.");
        return;
      }
      if (!file.type.startsWith("image/")) {
        alert("Please upload a valid image file.");
        return;
      }

      const reader = new FileReader();
      reader.onloadend = () => {
        setNewProfilePic(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className={`profile-container ${theme}`}>
      <div className="profile-card">
        {/* Cover Photo */}
        <div className="cover-photo"></div>

        {/* Profile Header */}
        <div className="profile-header">
          <img
            src={newProfilePic || user.profilePic}
            alt="Profile"
            className="profile-pic"
          />
          <h2>{user.name}</h2>
          <p className="email">{user.email}</p>
          <p className="tagline">{user.bio}</p>
        </div>

        {/* Social Media Links */}
        <div className="social-links">
          <a href="#"><i className="fab fa-github"></i></a>
          <a href="#"><i className="fab fa-linkedin"></i></a>
          <a href="#"><i className="fab fa-twitter"></i></a>
        </div>

        {/* Stats Section */}
        <div className="stats">
          <div className="stat">
            <span className="stat-number">1.2K</span>
            <span className="stat-label">Followers</span>
          </div>
          <div className="stat">
            <span className="stat-number">500</span>
            <span className="stat-label">Following</span>
          </div>
          <div className="stat">
            <span className="stat-number">45</span>
            <span className="stat-label">Posts</span>
          </div>
        </div>

        {/* Skills Section */}
        <div className="skills">
          <h3>Skills</h3>
          {user.skills.map((skill, index) => (
            <div className="skill" key={index}>
              <span>{skill.name}</span>
              <div className="progress-bar">
                <div className="progress" style={{ width: `${skill.progress}%` }}></div>
              </div>
            </div>
          ))}
        </div>

        {/* Edit Mode Fields */}
        {editMode && (
          <div className="edit-fields">
            <textarea
              value={newBio}
              onChange={(e) => setNewBio(e.target.value)}
              placeholder="Write something about yourself..."
            />
            <input
              type="text"
              value={newPhone}
              onChange={(e) => setNewPhone(e.target.value)}
              placeholder="Enter your phone number"
            />
            <input
              type="text"
              value={newLocation}
              onChange={(e) => setNewLocation(e.target.value)}
              placeholder="Enter your location"
            />
            <div className="upload-section">
              <label htmlFor="profile-pic-upload">Upload Profile Picture</label>
              <input
                id="profile-pic-upload"
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
              />
            </div>
          </div>
        )}

        {/* Buttons */}
        <div className="button-group">
          <button className="edit-btn" onClick={() => setEditMode(!editMode)}>
            {editMode ? "Cancel" : "Edit Profile"}
          </button>
          {editMode && (
            <button className="save-btn" onClick={handleSave}>
              Save Changes
            </button>
          )}
          <button className="theme-toggle" onClick={toggleTheme}>
            {theme === "light" ? "🌙" : "☀️"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Profile;
