import React, { useState, useEffect } from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";
import { FaUserCircle, FaBars, FaTimes, FaMoon, FaSun } from "react-icons/fa";
import { motion, AnimatePresence } from "framer-motion";

const Dashboard1 = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [darkMode, setDarkMode] = useState(() => localStorage.getItem("darkMode") === "true");
  const [username, setUsername] = useState(localStorage.getItem("username") || "");
  const [role, setRole] = useState("");

  const navigate = useNavigate();

  useEffect(() => {
    const storedRole = localStorage.getItem("role")?.trim().toLowerCase();
    setRole(storedRole || "");
  }, []);

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
    localStorage.setItem("darkMode", darkMode);
  }, [darkMode]);

  const toggleMenu = () => setMenuOpen(!menuOpen);
  const toggleDarkMode = () => setDarkMode((prev) => !prev);

  const handleLogout = () => {
    localStorage.clear();
    setUsername("");
    setRole("");
    navigate("/login");
    window.location.reload();
  };

  return (
    <div className="flex h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-gray-100">
      {/* Sidebar */}
      <motion.aside
        initial={{ x: -200 }}
        animate={{ x: 0 }}
        transition={{ duration: 0.3 }}
        className={`fixed top-0 left-0 h-full w-64 bg-white dark:bg-gray-800 shadow-md p-5 z-50 ${
          menuOpen ? "translate-x-0" : "-translate-x-64"
        } md:translate-x-0 md:relative`}
      >
        <div className="flex justify-between items-center">
          <h1 className="text-2xl font-bold">NoteHive</h1>
          <button onClick={toggleMenu} className="md:hidden text-xl">
            <FaTimes />
          </button>
        </div>
        <nav className="mt-5 space-y-2">
          <Link to="/" className="block py-2 px-4 rounded-md hover:bg-gray-200 dark:hover:bg-gray-700">Home</Link>
          <Link to="/about" className="block py-2 px-4 rounded-md hover:bg-gray-200 dark:hover:bg-gray-700">About</Link>
          <Link to="/notes" className="block py-2 px-4 rounded-md hover:bg-gray-200 dark:hover:bg-gray-700">Notes</Link>
          <Link to="/cp-trivia" className="block py-2 px-4 rounded-md hover:bg-gray-200 dark:hover:bg-gray-700">Trivia & Quizzes</Link>

          {role === "admin" && (
            <>
              <Link to="/admin" className="block py-2 px-4 rounded-md hover:bg-gray-200 dark:hover:bg-gray-700">Admin Panel</Link>
              <Link to="/admin/noteManager" className="block py-2 px-4 rounded-md hover:bg-gray-200 dark:hover:bg-gray-700">Note Manager</Link>
            </>
          )}

          {username ? (
            <div className="mt-5">
              <div className="flex items-center space-x-2 cursor-pointer">
                <FaUserCircle className="text-2xl" />
                <span>{username}</span>
              </div>
              <div className="mt-2 space-y-1">
                <button onClick={() => navigate("/profile")} className="block py-2 px-4 rounded-md hover:bg-gray-200 dark:hover:bg-gray-700 w-full text-left">
                  Profile
                </button>
                <button onClick={() => navigate("/settings")} className="block py-2 px-4 rounded-md hover:bg-gray-200 dark:hover:bg-gray-700 w-full text-left">
                  Settings
                </button>
                <button onClick={handleLogout} className="block py-2 px-4 rounded-md hover:bg-red-500 dark:hover:bg-red-600 w-full text-left">
                  Logout
                </button>
              </div>
            </div>
          ) : (
            <div className="mt-5">
              <Link to="/login" className="block py-2 px-4 rounded-md hover:bg-gray-200 dark:hover:bg-gray-700">Login</Link>
              <Link to="/register" className="block py-2 px-4 rounded-md hover:bg-gray-200 dark:hover:bg-gray-700">Sign Up</Link>
            </div>
          )}
        </nav>
      </motion.aside>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Top Bar */}
        <header className="w-full bg-white dark:bg-gray-800 shadow-md px-5 py-3 flex justify-between items-center">
          <button onClick={toggleMenu} className="md:hidden text-2xl">
            <FaBars />
          </button>

          {/* Dark Mode Toggle */}
          <button onClick={toggleDarkMode} className="text-2xl">
            <motion.div animate={{ rotate: darkMode ? 180 : 0 }} transition={{ duration: 0.3 }}>
              {darkMode ? <FaSun /> : <FaMoon />}
            </motion.div>
          </button>
        </header>

        {/* Main Content */}
        <motion.main
          className="p-5 flex-grow overflow-y-auto"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <Outlet />
        </motion.main>
      </div>
    </div>
  );
};

export default Dashboard1;
