import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Editor } from "react-draft-wysiwyg";
import { EditorState, convertToRaw } from "draft-js";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";
import { saveAs } from "file-saver";
import jsPDF from "jspdf";

const ManageNotes = ({ notes, deleteNote, updateNote }) => {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedNotes, setSelectedNotes] = useState([]);
  const [editorState, setEditorState] = useState(EditorState.createEmpty());
  const navigate = useNavigate();

  const handleDeleteSelected = () => {
    selectedNotes.forEach((id) => deleteNote(id));
    setSelectedNotes([]);
  };

  const handleExport = (format) => {
    const filteredNotes = notes.filter((note) =>
      note.title.toLowerCase().includes(searchQuery.toLowerCase())
    );
    
    if (format === "pdf") {
      const doc = new jsPDF();
      filteredNotes.forEach((note, index) => {
        doc.text(`${index + 1}. ${note.title}\n${note.content}\n`, 10, 10 + index * 20);
      });
      doc.save("notes.pdf");
    } else if (format === "txt") {
      const textContent = filteredNotes.map(note => `${note.title}\n${note.content}\n`).join("\n");
      const blob = new Blob([textContent], { type: "text/plain;charset=utf-8" });
      saveAs(blob, "notes.txt");
    }
  };

  return (
    <div>
      <h2>Manage Notes</h2>
      <input
        type="text"
        placeholder="Search Notes..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <button onClick={handleDeleteSelected} disabled={selectedNotes.length === 0}>
        Bulk Delete
      </button>
      <button onClick={() => handleExport("pdf")}>Export as PDF</button>
      <button onClick={() => handleExport("txt")}>Export as TXT</button>

      <ul>
        {notes
          .filter((note) =>
            note.title.toLowerCase().includes(searchQuery.toLowerCase())
          )
          .map((note) => (
            <li key={note.id}>
              <input
                type="checkbox"
                checked={selectedNotes.includes(note.id)}
                onChange={() =>
                  setSelectedNotes((prev) =>
                    prev.includes(note.id)
                      ? prev.filter((id) => id !== note.id)
                      : [...prev, note.id]
                  )
                }
              />
              <strong>{note.title}</strong>
              <p>{note.content}</p>
              <button onClick={() => navigate(`/admin/edit-note/${note.id}`)}>
                Edit
              </button>
              <button onClick={() => deleteNote(note.id)}>Delete</button>
            </li>
          ))}
      </ul>
    </div>
  );
};

export default ManageNotes;
