import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { useAuth } from "./context/AuthContext"; // Correct path
import "./Login.css";

const Login = ({ role = "user" }) => {
  const navigate = useNavigate();
  const { login } = useAuth(); // Use AuthContext
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [message, setMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [showForm, setShowForm] = useState(true);

  // Determine API endpoint & redirect path based on role
  const apiEndpoint = role === "admin" ? "http://localhost:5000/admin/login" : "http://localhost:5000/login";
  const redirectPath = role === "admin" ? "/admin" : "/";

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const response = await fetch(apiEndpoint, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        login(data.user); // Use AuthContext login
        setMessage(`${role.charAt(0).toUpperCase() + role.slice(1)} login successful! Redirecting...`);

        setTimeout(() => {
          navigate(redirectPath);
        }, 1500);
      } else {
        setMessage(data.message || "Invalid credentials. Try again.");
      }
    } catch (error) {
      setMessage("Server error. Please try again later.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleCloseForm = () => {
    setShowForm(false);
  };

  return (
    <>
      {showForm && (
        <div className="login-form-container">
          <button className="close-btn" onClick={handleCloseForm}>
            &times;
          </button>
          <h2>{role === "admin" ? "Admin Login" : "User Login"}</h2>
          <form onSubmit={handleSubmit} className="login-form">
            <div className="input-group">
              <input
                type="email"
                name="email"
                placeholder={role === "admin" ? "Admin Email" : "Email"}
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
            <div className="input-group">
              <input
                type="password"
                name="password"
                placeholder={role === "admin" ? "Admin Password" : "Password"}
                value={formData.password}
                onChange={handleChange}
                required
              />
            </div>
            <button type="submit" className="submit-btn" disabled={isLoading}>
              {isLoading ? "Logging in..." : "Login"}
            </button>
          </form>
          {message && <p className={message.includes("success") ? "success" : "error"}>{message}</p>}
          {role === "user" ? (
            <p>
              Don't have an account? <Link to="/register">Sign Up</Link>
            </p>
          ) : (
            <p>
              Not an admin? <Link to="/login">User Login</Link>
            </p>
          )}
        </div>
      )}
    </>
  );
};

export default Login;
