import React, { useState, useEffect } from "react";
import "../css/Profile.css";

const Profile = () => {
  const [user, setUser] = useState({
    name: localStorage.getItem("username") || "John Doe",
    email: localStorage.getItem("email") || "john.doe@example.com",
    bio: localStorage.getItem("bio") || "Turning ideas into reality, one line of code at a time.",
    profilePic: localStorage.getItem("profilePic") || "https://via.placeholder.com/150",
    phone: localStorage.getItem("phone") || "+1234567890",
    location: localStorage.getItem("location") || "New York, USA",
  });

  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({ ...user });

  // Handle input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle profile picture upload
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setFormData({ ...formData, profilePic: reader.result });
      };
      reader.readAsDataURL(file);
    }
  };

  // Save changes to localStorage and update state
  const handleSave = () => {
    Object.keys(formData).forEach((key) => {
      localStorage.setItem(key, formData[key]);
    });
    setUser(formData);
    setEditMode(false);
  };

  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-header">
          <img src={formData.profilePic} alt="Profile" className="profile-pic" />
          <h2>{user.name}</h2>
          <p className="email">{user.email}</p>
          <p className="bio">{user.bio}</p>
        </div>

        {editMode && (
          <div className="edit-fields">
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Full Name"
            />
            <textarea
              name="bio"
              value={formData.bio}
              onChange={handleChange}
              placeholder="Write about yourself..."
            />
            <input
              type="text"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              placeholder="Phone Number"
            />
            <input
              type="text"
              name="location"
              value={formData.location}
              onChange={handleChange}
              placeholder="Location"
            />
            <label className="upload-label">
              Upload Profile Picture
              <input type="file" accept="image/*" onChange={handleImageUpload} />
            </label>
          </div>
        )}

        <div className="button-group">
          <button className="edit-btn" onClick={() => setEditMode(!editMode)}>
            {editMode ? "Cancel" : "Edit Profile"}
          </button>
          {editMode && <button className="save-btn" onClick={handleSave}>Save Changes</button>}
        </div>
      </div>
    </div>
  );
};

export default Profile;
