import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Notes = () => {
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch notes from localStorage when the component mounts
  useEffect(() => {
    const fetchNotes = () => {
      const storedNotes = JSON.parse(localStorage.getItem("notes")) || [];
      setNotes(storedNotes);
      setLoading(false);
    };

    fetchNotes();
  }, []);

  // Show loading indicator while fetching data
  if (loading) {
    return <div className="text-center text-white text-lg">Loading notes...</div>;
  }

  return (
    <div className="bg-gradient-to-br from-gray-900 to-blue-900 p-12 rounded-2xl text-center shadow-2xl mt-10 min-h-screen flex flex-col justify-center text-white font-roboto">
      <h2 className="text-4xl font-bold uppercase tracking-wide mb-10">My Notes</h2>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8 justify-center">
        {notes.length === 0 ? (
          <p className="text-lg opacity-80">No notes available.</p>
        ) : (
          notes.map((note) => (
            <div
              key={note.id}
              className="w-72 h-80 perspective hover:scale-105 transition-transform duration-300"
            >
              <div className="relative w-full h-full transform-style-preserve-3d transition-transform duration-500 hover:rotate-y-180">
                {/* Front Side */}
                <div className="absolute w-full h-full bg-gradient-to-br from-gray-800 to-blue-400 text-white font-semibold text-xl flex justify-center items-center text-center p-5 rounded-xl shadow-xl backface-hidden">
                  <h3>{note.title}</h3>
                </div>

                {/* Back Side */}
                <div className="absolute w-full h-full bg-blue-700 flex flex-col justify-center items-center text-center p-5 rounded-xl shadow-xl rotate-y-180 backface-hidden">
                  <h3 className="mb-4 text-xl font-semibold">{note.title}</h3>
                  <Link
                    to={`/notes/${note.id}`}
                    className="inline-block px-4 py-2 bg-blue-500 text-white font-semibold text-lg rounded-lg transition-transform transform hover:scale-105 hover:bg-blue-600"
                  >
                    View Note
                  </Link>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Notes;
