const BASE_URL = "http://localhost:5000/api/notes"; // Update the URL if needed

export const getNotes = async () => {
  const response = await fetch(`${BASE_URL}`);
  return response.json();
};

export const addNote = async (noteData) => {
  const response = await fetch(`${BASE_URL}/add`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(noteData),
  });
  return response.json();
};

export const editNote = async (noteId, noteData) => {
  const response = await fetch(`${BASE_URL}/edit/${noteId}`, {
    method: "PUT",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(noteData),
  });
  return response.json();
};

export const deleteNote = async (noteId) => {
  await fetch(`${BASE_URL}/delete/${noteId}`, { method: "DELETE" });
};
