import React, { useState } from "react";
import "../css/FAQ.css"; // Import styling

const FAQ = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleFAQ = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="faq-container">
      {/* Floating Button */}
      <button className="faq-button" onClick={toggleFAQ}>
        ❓
      </button>

      {/* FAQ Popup */}
      {isOpen && (
        <div className="faq-popup">
          <div className="faq-header">
            <h3>Frequently Asked Questions</h3>
            <button className="close-btn" onClick={toggleFAQ}>✖</button>
          </div>
          <div className="faq-body">
            <p><strong>1. What is MyApp?</strong><br /> MyApp is a productivity app with real-time updates and an intuitive interface.</p>
            <p><strong>2. Does MyApp support Dark Mode?</strong><br /> Yes! You can switch between Light and Dark themes.</p>
            <p><strong>3. Can I use MyApp on mobile?</strong><br /> Yes! MyApp is fully responsive across all devices.</p>
            <p><strong>4. Does MyApp provide real-time notifications?</strong><br /> Yes! You will receive instant updates and alerts.</p>
            <p><strong>5. What technology powers MyApp?</strong><br /> MyApp is built with React.js for a smooth experience.</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default FAQ;
